INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`) VALUES
(66, 'Tết Ở Làng Địa Ngục', '001', '112689', 98, '1715065077_1.jpg', '<p>C&acirc;u chuyện kinh dị đậm chất Việt Nam, mang trải nghiệm mới lạ.</p>\r\n\r\n<p>T&aacute;c giả th&agrave;nh danh bằng những t&aacute;c phẩm kinh dị chất lượng.</p>\r\n\r\n<p>K&iacute;ch thước s&aacute;ch ph&ugrave; hợp v&agrave; dễ cầm nắm.</p>\r\n', '<p>&quot;Tết Ở L&agrave;ng Địa Ngục&quot; l&agrave; một cuốn s&aacute;ch kinh dị của t&aacute;c giả Thảo Trang. Cuốn s&aacute;ch kể về một ng&ocirc;i l&agrave;ng kinh h&atilde;i v&agrave; ho&agrave;i nghi trước bi kịch ập đến. Người duy nhất rời l&agrave;ng l&agrave; &ocirc;ng Thập, người nhận được một b&aacute;o mộng đen tối. Cuốn s&aacute;ch n&agrave;y mở ra thế giới huyền b&iacute; đầy sự hấp dẫn với những sinh vật si&ecirc;u linh v&agrave; sự kết hợp giữa kinh dị v&agrave; huyền b&iacute;. T&aacute;c phẩm n&agrave;y mang đậm chất Việt Nam v&agrave; l&agrave; một trong những t&aacute;c phẩm văn học kinh dị nổi tiếng của t&aacute;c giả Thảo Trang.ẫn.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 35),
(67, 'The Shinning ', '002', '210000', 92, '1715066577_2.png.webp', '<p>L&agrave; cuốn tiểu thuyết đầu ti&ecirc;n của Stephen King, đưa &ocirc;ng trở th&agrave;nh một trong những t&aacute;c giả kinh dị h&agrave;ng đầu.</p>\r\n\r\n<p>Được coi l&agrave; một trong những c&acirc;u chuyện si&ecirc;u nhi&ecirc;n xuất sắc nhất trong v&ograve;ng một trăm năm.</p>\r\n\r\n<p>Bộ phim chuyển thể năm 1980 do Stanley Kubrick đạo diễn cũng được coi l&agrave; một trong những bộ phim kinh dị xuất sắc nhất từng được l&agrave;m.</p>\r\n\r\n<p>T&aacute;c giả Stephen King l&agrave; &quot;&Ocirc;ng ho&agrave;ng Kinh dị&quot; với hơn 350 triệu bản s&aacute;ch đ&atilde; b&aacute;n được v&agrave; nhiều lần được dựng th&agrave;nh phim.</p>\r\n\r\n<p>Cuốn s&aacute;ch đ&atilde; nhận được nhiều giải thưởng danh gi&aacute; v&agrave; được xuất bản bởi Nh&atilde; Nam.</p>\r\n', '<p>Thị Kiến (The Shining, 1977) l&agrave; cuốn tiểu thuyết đầu ti&ecirc;n của Stephen King, đưa &ocirc;ng trở th&agrave;nh một trong những t&aacute;c giả kinh dị h&agrave;ng đầu. Bộ phim chuyển thể năm 1980 do Stanley Kubrick đạo diễn, vẫn được coi l&agrave; một trong những bộ phim kinh dị xuất sắc nhất từng được l&agrave;m.</p>\r\n', 1, 35),
(68, 'Hồ Tuyệt Mệnh', '003', '156232', 56, '1715068098_4.jpg.webp', '<p>Tiểu thuyết căng thẳng v&agrave; hồi hộp, thu h&uacute;t người đọc.</p>\r\n\r\n<p>T&aacute;c giả Stephen King tạo n&ecirc;n c&acirc;u chuyện sức hấp dẫn v&agrave; bất ngờ.</p>\r\n\r\n<p>B&igrave;a mềm v&agrave; k&iacute;ch thước s&aacute;ch ph&ugrave; hợp cho việc đọc.</p>\r\n', '<p>X&aacute;c chết của một cậu b&eacute; mười một tuổi bị x&acirc;m hại được ph&aacute;t hiện trong một c&ocirc;ng vi&ecirc;n của thị trấn. C&aacute;c nh&acirc;n chứng v&agrave; dấu v&acirc;n tay đều nhắm thẳng v&agrave;o nghi phạm duy nhất, một trong những c&ocirc;ng d&acirc;n nổi tiếng nhất của th&agrave;nh phố Flint &ndash; Terry Maitland, huấn luyện vi&ecirc;n giải b&oacute;ng ch&agrave;y Little League, gi&aacute;o vi&ecirc;n tiếng Anh, chồng của Marcy Maitland v&agrave; cha của hai c&ocirc; con g&aacute;i. Th&aacute;m tử Ralph Anderson, người c&oacute; con trai từng được Maitland huấn luyện, đ&atilde; ra lệnh bắt giữ c&ocirc;ng khai anh ta ngay lập tức. Maitland c&oacute; bằng chứng ngoại phạm, nhưng Anderson v&agrave; ủy vi&ecirc;n c&ocirc;ng tố quận cũng c&oacute; trong tay bằng chứng DNA tr&ugrave;ng khớp. Vụ &aacute;n rơi v&agrave;o bế tắc.&nbsp;</p>\r\n\r\n<p>Khi cuộc điều tra mở rộng v&agrave; những chi tiết kinh ho&agrave;ng bắt đầu xuất hiện, c&acirc;u chuyện của King bắt đầu lao đi với tốc độ rất nhanh, tạo ra sự căng thẳng mạnh mẽ v&agrave; sự hồi hộp gần như kh&ocirc;ng thể thở được. Terry Maitland c&oacute; vẻ l&agrave; một ch&agrave;ng trai tốt bụng, nhưng c&oacute; phải anh ta c&ograve;n mang một khu&ocirc;n mặt kh&aacute;c?</p>\r\n', 1, 35),
(69, 'Đũa ', '004', '243000', 72, '1715069781_5.jpg.webp', '<p>T&aacute;c phẩm thuộc thể loại truyện kinh dị, mang đến những c&acirc;u chuyện huyền b&iacute; xoay quanh đũa.</p>\r\n\r\n<p>Cuốn s&aacute;ch được phỏng t&aacute;c bởi năm t&aacute;c giả đến từ Nhật Bản, Hồng K&ocirc;ng, Đ&agrave;i Loan.</p>\r\n\r\n<p>Cung cấp những th&ocirc;ng tin về những tục lệ d&acirc;n gian huyền b&iacute; v&agrave; truyền thuyết về b&ugrave;a ngải, ma thuật.</p>\r\n\r\n<p>Ra mắt v&agrave;o m&ugrave;a h&egrave; năm nay v&agrave; được xuất bản bởi nh&agrave; xuất bản Phương Nam Book.</p>\r\n', '<p>L&agrave; một vật dụng rất đỗi thường mọn trong đời sống hằng ng&agrave;y nhưng với văn h&oacute;a &Aacute; Đ&ocirc;ng, đũa lại mang một sức mạnh t&acirc;m linh b&iacute; ẩn, h&agrave;m chứa v&ocirc; số những điều cấm kỵ truyền miệng suốt ng&agrave;n đời nay. Lấy cảm hứng từ điều đ&oacute;, năm t&aacute;c giả đến từ Nhật Bản, Hồng K&ocirc;ng, Đ&agrave;i Loan đ&atilde; c&ugrave;ng nhau phỏng t&aacute;c về những tục lệ d&acirc;n gian huyền b&iacute; cũng như những truyền thuyết về b&ugrave;a ngải, ma thuật xoay quanh vật phẩm b&eacute; nhỏ n&agrave;y qua một loạt vụ &aacute;n li&ecirc;n tiếp trải d&agrave;i từ qu&aacute; khứ đến hiện đại trong t&aacute;c phẩm&hellip;</p>\r\n\r\n<p>Đũa &ndash; Chuyện linh dị, ra mắt độc giả v&agrave;o m&ugrave;a h&egrave; năm nay.</p>\r\n', 1, 35),
(70, 'Ngôi Làng Cổ Mộ', '005', '157200', 102, '1715071751_11.jpg.webp', '<p>C&acirc;u chuyện r&ugrave;ng rợn, gay cấn v&agrave; căng thẳng.</p>\r\n\r\n<p>Ng&ocirc;n ngữ sắc sảo v&agrave; ch&acirc;n thực, tạo cảm gi&aacute;c lạc v&agrave;o thế giới u linh.</p>\r\n\r\n<p>T&aacute;c phẩm đề cao những gi&aacute; trị nh&acirc;n văn v&agrave; t&igrave;nh y&ecirc;u bản năng.</p>\r\n', '<p>Nếu l&agrave; một người y&ecirc;u th&iacute;ch tiểu thuyết kinh dị, bạn sẽ kh&ocirc;ng thể bỏ qua Ng&ocirc;i l&agrave;ng cổ mộ của t&aacute;c giả trẻ Thục Linh.</p>\r\n\r\n<p>Những chi tiết r&ugrave;ng rợn, những t&igrave;nh huống gay cấn, căng thẳng, sợ h&atilde;i, g&oacute;c nh&igrave;n trong cuộc của người đang phải chạy trốn hay đương đầu với những thế lực huyền b&iacute; ở Ng&ocirc;i l&agrave;ng cổ mộ sẽ l&ocirc;i cuốn bạn kh&ocirc;ng ngừng. Từng c&acirc;u chữ của Thục Linh sẽ dẫn dắt bạn đọc như lạc v&agrave;o ch&iacute;nh xứ sở u linh trong c&acirc;u chuyện, trải nghiệm cảm gi&aacute;c gh&ecirc; rợn một c&aacute;ch ch&acirc;n thực nhất như ch&iacute;nh c&aacute;c nh&acirc;n vật.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ dừng lại ở yếu tố kinh dị, điều đặc sắc hơn cả l&agrave; đằng sau bức m&agrave;n c&acirc;u chuyện huyền b&iacute; t&acirc;m linh, Thục Linh sẽ kể cho độc giả một bức tranh đời thực trần trụi nhất, sự đương đầu giữa c&aacute;i &aacute;c v&agrave; c&aacute;i thiện trong cuộc sống, t&igrave;nh y&ecirc;u thương v&agrave; x&uacute;c cảm bản năng nhất của con người, những gi&aacute; trị nh&acirc;n văn m&agrave; đ&ocirc;i l&uacute;c dường như ta đ&atilde; v&ocirc; t&igrave;nh l&atilde;ng qu&ecirc;n&hellip;</p>\r\n\r\n<p>---</p>\r\n\r\n<p>&ldquo;T&ocirc;i vội chạy ra xem th&igrave; chợt nhận ra những l&agrave;n kh&oacute;i mờ đục đang lẩn khuất khắp con đường l&agrave;ng nứt nẻ. Đ&oacute; l&agrave; sương m&ugrave;. Sương m&ugrave; d&acirc;ng cao v&agrave; ng&agrave;y c&agrave;ng d&agrave;y đặc hơ&rdquo;</p>\r\n\r\n<p>&ldquo;T&ocirc;i bước theo sau mẹ, tiến gần đến ng&ocirc;i nh&agrave; mới của ch&uacute;ng t&ocirc;i m&agrave; kh&ocirc;ng biết rằng t&ocirc;i đang từng bước lại gần những cơn &aacute;c mộng dai dẳng sau n&agrave;&rdquo;</p>\r\n\r\n<p>&ldquo;T&ocirc;i &ocirc;m chặt hai c&aacute;nh tay rồi đi gần như chạy qua cầu, l&ograve;ng dấy l&ecirc;n ch&uacute;t sợ h&atilde;i.</p>\r\n\r\n<p>C&acirc;y cầu khẽ rung l&ecirc;n trong từng bước ch&acirc;n. Khi t&ocirc;i tiến lại gần ph&iacute;a thằng b&eacute; th&igrave; &aacute;nh mắt v&ocirc; t&igrave;nh đảo xuống dưới ch&acirc;n. Qua một chiếc lỗ nứt nẻ dưới tấm v&aacute;n gỗ l&oacute;t cầu đ&atilde; mục, một con mắt trắng d&atilde; đục ngầu đang nh&igrave;n l&ecirc;n t&ocirc;i chằm chằm&hellip;&rdquo;</p>\r\n', 1, 35),
(71, 'Bóng Trăng Trắng Ngà', '006', '77900', 43, '1715070263_6.jpg.webp', '<p>C&acirc;u chuyện cảm động về t&igrave;nh bạn qua b&agrave;i h&aacute;t b&iacute; ẩn.</p>\r\n\r\n<p>Ng&ocirc;n ngữ s&acirc;u sắc, th&acirc;n thiện v&agrave; gần gũi.</p>\r\n\r\n<p>Tạo ra những cảm x&uacute;c mạnh mẽ v&agrave; đau đớn trong l&ograve;ng người đọc.</p>\r\n', '<p>Ẩn sau lời b&agrave;i h&aacute;t du dương &ecirc;m ả, vang l&ecirc;n mỗi độ Trung Thu về, lại ẩn chứa những c&acirc;u chuyện đau thương v&agrave; đầy gh&ecirc; rợ&nbsp;<br />\r\nB&agrave;i h&aacute;t &ldquo;Thằng Cuội&rdquo; gợi l&ecirc;n tuổi thơ ph&aacute; cỗ ngắm trăng tươi đẹp của bao người lại c&oacute; thể buồn day dứt tới vậy. Liệu c&oacute; khi n&agrave;o, bạn lắng nghe b&agrave;i h&aacute;t ấy v&agrave; tự hỏi, n&oacute; c&oacute; &yacute; nghĩa g&igrave; hay chưa?&nbsp;</p>\r\n\r\n<p>&ldquo;S&aacute;ng rơi xuống đồi&nbsp;<br />\r\nS&aacute;ng leo l&ecirc;n c&acirc;y&nbsp;<br />\r\nS&aacute;ng mỏi ch&acirc;n rồi&nbsp;<br />\r\nS&aacute;ng ngồi xuống đ&acirc;&rdquo;&nbsp;<br />\r\nBảo cũng như vậy.&nbsp;</p>\r\n\r\n<p>Một cậu b&eacute; mới chỉ 9 tuổi, đ&atilde; bị thu h&uacute;t bởi giọng h&aacute;t tuyệt vời của một c&ocirc; b&eacute; b&iacute; ẩn. C&ocirc; b&eacute; t&ecirc;n Chi, xuất hiện giữa một rừng đom đ&oacute;m đẹp như mộng. T&igrave;nh bạn nhanh ch&oacute;ng nảy nở giữa hai đứa trẻ chỉ bằng một b&agrave;i h&aacute;t. Chi hay kể cho Bảo nghe những c&acirc;u chuyện k&igrave; dị ph&iacute;a sau từng lời b&agrave;i h&aacute;t c&ocirc; b&eacute; hay h&aacute;t. Những tưởng rằng đ&oacute; chỉ l&agrave; những c&acirc;u chuyện kể vui vẻ thế nhưng, dường như, nh&acirc;n vật trong từng c&acirc;u chuyện đ&oacute; lại dần dần hiện hữu trong cuộc sống thực của Bảo. Một b&oacute;ng ma kh&ocirc;ng đầu&hellip; Một con ma đ&oacute;i&hellip;&nbsp;</p>\r\n\r\n<p>Sức khỏe của Bảo sa s&uacute;t, người th&acirc;n trong gia đ&igrave;nh lo lắng, sợ h&atilde;i, thậm ch&iacute; l&agrave; rơi v&agrave;o v&ograve;ng nguy hiểm</p>\r\n\r\n<p>Chuyện g&igrave; đang thực sự xảy ra? Mục đ&iacute;ch của c&ocirc; b&eacute; Chi l&agrave; g&igrave;? C&ocirc; b&eacute; đ&oacute;ng vai tr&ograve; g&igrave; trong những c&acirc;u chuyện kể ấy, với những g&igrave; đang xảy ra với Bảo? Liệu Bảo c&oacute; thể vượt qua cơn &aacute;c mộng v&agrave; cứu lấy gia đ&igrave;nh m&igrave;nh kh&ocirc;ng?&nbsp;</p>\r\n\r\n<p>Khởi đầu l&agrave; nỗi sợ h&atilde;i, &aacute;m ảnh. Kết lại cuốn s&aacute;ch, lại l&agrave; những cảm x&uacute;c đau đớn tới x&eacute; l&ograve;ng. L&ograve;ng trắc ẩn của bạn sẽ rung l&ecirc;n những sợi d&acirc;y cảm x&uacute;c, để c&ugrave;ng kh&oacute;c với những nh&acirc;n vật.&nbsp;</p>\r\n\r\n<p>Liệu t&agrave; &aacute;c c&oacute; tồn tại hay kh&ocirc;ng?&nbsp;</p>\r\n\r\n<p>Tất cả c&acirc;u trả lời sẽ c&oacute; trong cuốn s&aacute;ch &ldquo;B&oacute;ng trăng trắng ng&agrave;&rdquo; của nh&agrave; văn Thục Linh, một ấn phẩm tuyệt vời để nh&acirc;m nhi v&agrave;o dịp Trung thu. Nếu bạn đ&atilde; từng y&ecirc;u th&iacute;ch ng&ograve;i b&uacute;t của Thục Linh qua những ấn phẩm trước đ&oacute; như &ldquo;Ng&ocirc;i l&agrave;ng cổ mộ&rdquo; hay &ldquo;Tứ trấn huyền linh&rdquo; th&igrave; chắc chắn kh&ocirc;ng thể bỏ qua cuốn s&aacute;ch thứ ba n&agrave;y.</p>\r\n', 1, 35),
(72, 'Rừng Than Khóc', '007', '125200', 52, '1715070654_7.jpg.webp', '', '<p>&ldquo;Trường Sinh ch&oacute;t v&oacute;t non cao&nbsp;<br />\r\nKẻ đi săn kiếm cớ sao chẳng về?&rdquo;&nbsp;</p>\r\n\r\n<p>Ngải Trường Sinh l&agrave; lo&agrave;i c&acirc;y c&oacute; c&ocirc;ng dụng cải tử ho&agrave;n sinh v&agrave; k&eacute;o d&agrave;i tuổi thọ cho con người, nhưng những kẻ t&igrave;m kiếm n&oacute; sẽ phải vất vả, hi sinh cả thời gian, xương m&aacute;u, nước mắt v&agrave; thậm ch&iacute; l&agrave; ch&iacute;nh sinh mạng của m&igrave;nh để đ&aacute;nh đổi.</p>\r\n\r\n<p>S&aacute;u con người xa lạ từ những miền qu&ecirc; kh&aacute;c nhau đ&atilde; hội ngộ tại v&ugrave;ng n&uacute;i Ngoạn v&igrave; sức hấp dẫn, lời mời gọi của loại thần dược c&oacute; khả năng thay đổi số phận con người ấy. Một đo&agrave;n người săn t&igrave;m ngải đ&atilde; được lập n&ecirc;n, theo sự dẫn dắt của trưởng đo&agrave;n l&agrave; H&ugrave;ng, tiến s&acirc;u v&agrave;o v&ugrave;ng rừng xa lạ. Tự tin với việc đ&atilde; tận tay chạm v&agrave;o ngải Trường Sinh, dẫn nhiều đo&agrave;n kh&aacute;ch trở về an to&agrave;n, H&ugrave;ng phổ biến c&aacute;c quy tắc ngầm khi tiến v&agrave;o nơi &ldquo;rừng thi&ecirc;ng nước độc&rdquo;, y&ecirc;n t&acirc;m rằng mọi người sẽ lưu &yacute; v&agrave; l&agrave;m theo.</p>\r\n\r\n<p>Nhưng chưa khởi h&agrave;nh được bao l&acirc;u, những sự việc qu&aacute;i dị đột ngột xảy ra, liệu n&oacute; bắt đầu từ sự coi thường ph&eacute;p tắc tại chốn rừng thi&ecirc;ng của những kẻ mang suy nghĩ thiển cận, hay ch&iacute;nh tại nơi đ&acirc;y c&ograve;n ẩn n&aacute;u một sinh vật b&iacute; ẩn n&agrave;o đ&oacute;, gieo rắc c&aacute;i chết v&agrave; nỗi kinh ho&agrave;ng khiến đo&agrave;n người l&acirc;m v&agrave;o thảm cảnh đầy hung hiểm?</p>\r\n', 1, 35),
(73, 'Bức Tranh Kỳ Quái', '008', '148500', 82, '1715070841_8.jpg.webp', '', '<p>Bức tranh c&ocirc; g&aacute;i đứng trong gi&oacute; được đăng tải tr&ecirc;n một trang blog nọ, lại c&oacute; Bức tranh bị nh&ograve;e v&agrave; b&oacute;ng mờ bao phủ căn hộ do một đứa b&eacute; bị mất t&iacute;ch vẽ, c&ograve;n cả Bức tranh phong cảnh đồi n&uacute;i với những n&eacute;t vẽ run rẩ<br />\r\nNhững bức tranh vẽ tưởng chừng như v&ocirc; thưởng v&ocirc; phạt, cũng chẳng hề li&ecirc;n quan g&igrave; đến Tất cả tổng cộng 9 bức tranh vẽ tay ẩn chứa những &ldquo;c&acirc;u đố b&iacute; ẩn&rdquo; chờ đợi được giải m&atilde;.<br />\r\nRốt cuộc những người vẽ ra ch&uacute;ng đ&atilde; muốn truyền tải th&ocirc;ng điệp g&igrave;?<br />\r\nSự thật chấn động n&agrave;o được ch&ocirc;n giấu ph&iacute;a sau 9 bức vẽ kỳ lạ ấy?<br />\r\nSẽ ra sao khi những mảnh gh&eacute;p b&iacute; ẩn rời rạc nọ bỗng dưng được x&acirc;u chuỗi lại v&agrave; li&ecirc;n kết với nhau để mở ra một c&aacute;i kết chẳng thể ngờ tới?</p>\r\n', 1, 35),
(74, 'Trại Hoa Đỏ', '009', '165200', 47, '1715070964_9.jpg.webp', '<p>Cốt truyện hấp dẫn v&agrave; đầy bất ngờ.</p>\r\n\r\n<p>M&ocirc; tả chi tiết v&agrave; tạo h&igrave;nh nh&acirc;n vật sắc n&eacute;t.</p>\r\n\r\n<p>Tạo ra kh&ocirc;ng gian kinh ho&agrave;ng v&agrave; b&iacute; ẩn tại Trại Hoa Đỏ.</p>\r\n', '<p><em>&ldquo;Trại Hoa Đỏ</em><em>&nbsp;được ph&aacute;t h&agrave;nh lần đầu ti&ecirc;n năm 2009 nhưng bản thảo t&aacute;c phẩm đ&atilde; được nh&agrave; văn Di Li đăng tải dần l&ecirc;n mạng từ th&aacute;ng 10 năm 2007. Qua blog c&aacute; nh&acirc;n của m&igrave;nh, Di Li lần lượt giới thiệu từng chương truyện, chỉ giấu hai chương mở n&uacute;t cuối c&ugrave;ng. Với kết cấu chặt chẽ, mạch truyện k&iacute;n, Di Li kể cho người đọc một c&acirc;u chuyện kh&ocirc;ng hề dễ đo&aacute;n như c&aacute;c t&aacute;c phẩm trinh th&aacute;m th&ocirc;ng thường của Việt Nam.</em></p>\r\n\r\n<p><em>Nh&acirc;n vật ch&iacute;nh của tiểu thuyết&nbsp;Trại Hoa Đỏ&nbsp;l&agrave; Di&ecirc;n Vĩ, một thiếu phụ sang trọng v&agrave; xinh đẹp. C&acirc;u chuyện bắt đầu từ chỗ Di&ecirc;n Vĩ được chồng tặng cho một trang trại nằm lọt giữa một v&ugrave;ng rừng n&uacute;i &acirc;m u, hẻo l&aacute;nh. Ngay khi đặt ch&acirc;n đến trang trại c&oacute; nhiều lo&agrave;i hoa đỏ nở k&iacute;n rừng một c&aacute;ch huyền b&iacute;, Di&ecirc;n Vĩ đ&atilde; thấy c&oacute; rất nhiều những cảm gi&aacute;c bất an v&acirc;y bủa xung quanh m&igrave;nh. Những người bản địa thuộc một d&acirc;n tộc thiểu số k&igrave; dị; những c&aacute;i chết b&iacute; ẩn xảy ra li&ecirc;n tục; những truyền thuyết ma qu&aacute;i về d&ograve;ng họ Qu&aacute;ch&hellip; đ&atilde; khiến cho Di&ecirc;n Vĩ thực sự sợ h&atilde;i v&agrave; kinh ho&agrave;ng trong suốt qu&atilde;ng thời gian c&oacute; mặt ở Trại Hoa Đỏ. Trong bối cảnh đ&oacute;, cảnh s&aacute;t h&igrave;nh sự Phan Đăng B&aacute;ch, một kh&aacute;ch mời của trang trại trong ng&agrave;y kh&aacute;nh th&agrave;nh, t&igrave;nh cờ trở th&agrave;nh th&aacute;m tử điều tra những c&aacute;i chết b&iacute; ẩn ở Trại Hoa Đỏ. C&ugrave;ng thời điểm ấy, người bạn th&acirc;n nhất của anh bị s&aacute;t hại khi đang điều tra một tổ chức tội phạm xuy&ecirc;n quốc gia&hellip;&rdquo;</em></p>\r\n\r\n<p>Trại Hoa Đỏ đang được đạo diễn Victor Vũ chuyển thể th&agrave;nh phim d&agrave;i tập &ndash; Bộ phim d&agrave;i tập được đầu tư lớn nhất Việt Nam, ra mắt v&agrave;o cuối năm 2022.</p>\r\n\r\n<p>Với lần t&aacute;i bản Trại hoa đỏ lần thứ 6 n&agrave;y, Li&ecirc;n Việt tr&acirc;n trọng giới thiệu đến qu&yacute; độc giả Tiểu thuyết trinh th&aacute;m kinh dị&nbsp;<strong>Trại Hoa đỏ</strong>&nbsp;với hai phi&ecirc;n bản ph&aacute;t h&agrave;nh, ấn bản b&igrave;a cứng số lượng giới hạn v&agrave; ấn bản b&igrave;a mềm th&ocirc;ng thường. Quy c&aacute;ch s&aacute;ch cụ thể như sau:</p>\r\n\r\n<p>Quy c&aacute;ch chung khổ s&aacute;ch 16 x 24 cm, 464 trang, NXB Văn Học, ruột s&aacute;ch in giấy ng&agrave; v&agrave;ng nhập khẩu; Bản b&igrave;a cứng b&igrave;a in 5 m&agrave;u nhũ, &eacute;p kim, b&igrave;a &aacute;o in 4 m&agrave;u &eacute;p kim, phủ lax t&ecirc;n s&aacute;ch.</p>\r\n\r\n<p>K&iacute;nh mời c&aacute;c bạn đ&oacute;n đọc.</p>\r\n', 1, 35),
(75, 'Hannibals', '010', '120000', 53, '1715071225_10.jpg.webp', '', '<p>Được xem l&agrave; một trong những sự kiện văn chương được chờ đợi nhất, Hannibal mang người đọc v&agrave;o cung điện k&yacute; ức của một kẻ ăn thịt người, tạo dựng n&ecirc;n một bức ch&acirc;n dung ớn lạnh của tội &aacute;c đang &acirc;m thầm sinh s&ocirc;i - một th&agrave;nh c&ocirc;ng của thể loại kinh dị t&acirc;m l&yacute;.<br />\r\n<br />\r\nVới Mason Verger, nạn nh&acirc;n đ&atilde; bị Hannibal biến th&agrave;nh kẻ người kh&ocirc;ng ra người, Hannibal l&agrave; mối hận th&ugrave; nhức nhối da thịt.<br />\r\n<br />\r\nVới đặc vụ Clarice Starling của FBI, người từng thẩm vấn Hannibal trong trại t&acirc;m thần, giọng kim ken k&eacute;t của hắn vẫn vang vọng trong giấc mơ c&ocirc;.<br />\r\n<br />\r\nVới cảnh s&aacute;t Rinaldo Pazzi đang thất thế, Lecter hứa hẹn mang tới một khoản tiền b&eacute;o bở để đổi vận.<br />\r\n<br />\r\nV&agrave; những cuộc săn l&ugrave;ng Hannibal Lecter bắt đầu, k&eacute;o theo đ&oacute; l&agrave; những chuỗi ng&agrave;y run rẩy h&ograve;ng chấm dứt bảy năm tự do của hắn. Nhưng trong ba kẻ đi săn, chỉ một kẻ c&oacute; bản lĩnh sống trụ lại để hưởng th&agrave;nh quả của m&igrave;nh.</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. B&ecirc;n cạnh đ&oacute;, tuỳ v&agrave;o loại sản phẩm, h&igrave;nh thức v&agrave; địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, thuế nhập khẩu (đối với đơn h&agrave;ng giao từ nước ngo&agrave;i c&oacute; gi&aacute; trị tr&ecirc;n 1 triệu đồng).....</p>\r\n', 1, 35),
(76, 'Ác Duyên - Duyên khởi', '011', '131750', 34, '1715071505_011.png.webp', '<p>&Aacute;C DUY&Ecirc;N &ndash; DUY&Ecirc;N KHỞI</p>\r\n\r\n<p>T&aacute;c giả: Rosie Nguyễn - Linh Lan</p>\r\n\r\n<p>Nh&agrave; ph&aacute;t h&agrave;nh: Linh Lan Books</p>\r\n\r\n<p>NXB Thanh ni&ecirc;n</p>\r\n\r\n<p>M&atilde; ISBN: 8936213490715</p>\r\n\r\n<p>***</p>\r\n\r\n<p>Th&ocirc;ng số cơ bản:</p>\r\n\r\n<p>H&igrave;nh thức: b&igrave;a mềm</p>\r\n\r\n<p>K&iacute;ch thước: 14x20,5 cm</p>\r\n\r\n<p>Số trang: 300 trang</p>\r\n\r\n<p>Khối lượng: 500gr</p>\r\n\r\n<p>Năm ph&aacute;t h&agrave;nh: 2023</p>\r\n', '<p>Ở thị x&atilde; ấy, c&oacute; một ng&ocirc;i miếu được cho l&agrave; &ldquo;tuyệt đối linh thi&ecirc;ng&rdquo;, gần như cầu g&igrave; được nấy, nhất l&agrave; với những người c&oacute; dục vọng v&agrave; manh t&acirc;m mạnh mẽ. Lộc l&agrave; một g&atilde; cục mịch, mưu sinh ch&iacute;nh bằng nghề &ldquo;chăn dắt&rdquo; c&aacute;c trẻ em lang thang b&aacute;n kẹo rong. Hắn bắt sống con m&egrave;o hoang đang c&oacute; chửa luẩn quẩn ở gần miếu, g&oacute;i v&agrave;o bao tải v&agrave; đ&aacute;nh đập đến khi kh&ocirc;ng c&ograve;n hơi thở. Khi bước ch&acirc;n về nh&agrave;, Lộc hoảng hốt ph&aacute;t hiện người vợ t&agrave;n tật của m&igrave;nh đ&atilde; bị giết hại một c&aacute;ch b&iacute; ẩn. Nghi ngờ đ&aacute;m trẻ b&aacute;n kẹo h&atilde;m hại để trả th&ugrave;, Lộc nổi đi&ecirc;n tr&oacute;i, nhốt ch&uacute;ng v&agrave;o l&aacute;n t&ocirc;n sau nh&agrave;. Nhưng nửa đ&ecirc;m, điều k&igrave; qu&aacute;i bắt đầu xảy đến: khi tỉnh ngủ sau 5 gi&acirc;y chợp mắt, hắn ph&aacute;t hiện m&igrave;nh đ&atilde; trở th&agrave;nh một người ho&agrave;n to&agrave;n kh&aacute;c&hellip; Nhưng đ&oacute; mới chỉ l&agrave; bắt đầu&hellip; cứ bốn ng&agrave;y một lần, Lộc bị &ldquo;nhảy hồn&rdquo; sang một kiếp sống mới trong h&igrave;nh h&agrave;i của một người kh&aacute;c.</p>\r\n\r\n<p>Kh&ocirc;ng phải chịu tr&aacute;ch nhiệm về danh t&iacute;nh, Lộc trở th&agrave;nh một s&aacute;t thủ li&ecirc;n ho&agrave;n.</p>\r\n\r\n<p>Hắn phải lựa chọn: tiếp tục g&acirc;y nghiệp &aacute;c, hắn sẽ được qu&ecirc;n đi v&agrave; sang kiếp sống mới với c&aacute;i đầu được tẩy rỗng; hoặc ngừng tay nhưng lại t&iacute;ch lũy th&ecirc;m đau khổ v&agrave; &aacute;m ảnh. Kh&ocirc;ng c&ograve;n đường quay đầu, v&agrave; bước tiếp th&igrave; vẫn lu&acirc;n hồi trong tuyệt vọng, Lộc c&oacute; t&igrave;m c&aacute;ch tho&aacute;t ra hay tiếp tục chuỗi đời lang bạt kh&ocirc;ng danh t&iacute;nh, kh&ocirc;ng qu&ecirc; hương của m&igrave;nh? Những b&iacute; ẩn bao tr&ugrave;m, tiếp nối, xoay quanh v&agrave; hướng về một con Linh Mi&ecirc;u đ&aacute;ng sợ&hellip;</p>\r\n\r\n<p>&ldquo;C&acirc;u chuyện mang đậm triết l&yacute; của d&acirc;n tộc Việt Nam, những t&iacute;n ngưỡng đ&atilde; c&oacute; từ ng&agrave;n xưa như: thờ th&agrave;nh ho&agrave;ng l&agrave;ng, người gặp người đều do nh&acirc;n duy&ecirc;n tiền đị Một t&aacute;c phẩm ph&ugrave; hợp với cả thanh thiếu ni&ecirc;n v&agrave; những người luống tuổi.&quot;</p>\r\n', 1, 35),
(77, 'Tứ Trấn Huyền Linh', '012', '210473', 71, '1715071925_12.jpg.webp', '', '<p>Bỗng một ng&agrave;y tỉnh dậy, bạn ph&aacute;t hiện một bản sao giống y hệt m&igrave;nh. Người đ&oacute; kh&ocirc;ng phải anh chị em sinh đ&ocirc;i, lại c&agrave;ng kh&ocirc;ng phải họ h&agrave;ng th&acirc;n th&iacute;ch của bạn? Thế nhưng từ trang phục, kiểu t&oacute;c đến c&aacute;ch h&agrave;nh xử lại giống bạn đến lạ thường. Nếu bất chợt bị rơi v&agrave;o t&igrave;nh cảnh đ&oacute;, bạn sẽ chọn c&aacute;ch xử tr&iacute; như thế n&agrave;o?&nbsp;</p>\r\n\r\n<p>Đ&oacute; cũng ch&iacute;nh l&agrave; t&igrave;nh huống &eacute;o le m&agrave; ch&agrave;ng nh&acirc;n vật ch&iacute;nh trong Tứ Trấn Huyền Linh gặp phải. Tĩnh &ndash; ch&agrave;ng sinh vi&ecirc;n trường Y với những ước mơ v&agrave; ho&agrave;i b&atilde;o lớn về tương lai v&agrave; sự nghiệp bỗng bị &aacute;m bởi lời nguyền từ kẻ nhị tr&ugrave;ng, một bản sao y hệt của ch&iacute;nh anh. L&agrave; một người tin v&agrave;o khoa học, tin v&agrave;o thực tế, thế nhưng trải qua biết bao sự kiện dị thường, ma mị, ch&iacute;nh Tĩnh cũng phải thừa nhận về sự hiện diện của một thế giới t&acirc;m linh k&igrave; ảo v&agrave; phải nương nhờ những thần linh ở c&aacute;c trấn Thăng Long để gi&uacute;p h&oacute;a giải lời nguyền b&iacute; ẩn kia.&nbsp;</p>\r\n\r\n<p>Mỗi v&ugrave;ng đất, mỗi con người trong Tứ Trấn Huyền Linh đều chứa đựng những c&acirc;u chuyện kh&oacute; đo&aacute;n ph&iacute;a sau n&oacute;. C&ugrave;ng theo ch&acirc;n Tĩnh v&agrave;o những chuyến &ldquo;du kh&ocirc;ng&rdquo;, ta biết được v&ocirc; v&agrave;n những sự kiện lịch sử v&agrave; những ẩn t&igrave;nh đầy đau thương. Đ&oacute; l&agrave; c&ocirc; c&ocirc;ng ch&uacute;a dốc hết t&acirc;m can, b&agrave;y đủ mưu kế để chiếm được tr&aacute;i tim người m&igrave;nh y&ecirc;u. Một vị vua lỗi lạc kh&ocirc;ng may sập bẫy gian t&agrave; dẫn đến họa vong quốc. Hay l&agrave; kẻ &ocirc;m mộng lớn, đến l&igrave;a đời vẫn c&ograve;n m&atilde;i tiếc thương&hellip;&nbsp;</p>\r\n\r\n<p>H&agrave;nh tr&igrave;nh h&oacute;a giải lời nguyền của Tĩnh c&ograve;n c&oacute; sự gi&uacute;p đỡ v&ocirc; c&ugrave;ng nhiệt t&igrave;nh từ V&acirc;n với biệt danh &ldquo;b&agrave; c&ocirc; lịch sử&rdquo; c&ugrave;ng với Duy &ndash; anh bạn c&ugrave;ng qu&ecirc; l&eacute;m lỉnh, lu&ocirc;n ở b&ecirc;n cạnh v&agrave; sẵn s&agrave;ng gi&uacute;p đỡ Tĩnh kh&ocirc;ng ngại kh&oacute; khăn. Những c&acirc;u chuyện xoay quanh ba người bạn n&agrave;y kh&ocirc;ng chỉ khiến ch&uacute;ng ta ngạc nhi&ecirc;n về c&aacute;ch họ c&ugrave;ng nhau t&igrave;m ra giải ph&aacute;p cho những vấn đề t&acirc;m linh b&iacute; ẩn m&agrave; c&ograve;n khiến ch&uacute;ng ta v&ocirc; c&ugrave;ng ngưỡng mộ về t&igrave;nh bạn đẹp, trong s&aacute;ng.&nbsp;</p>\r\n\r\n<p>Với lối viết v&ocirc; c&ugrave;ng l&ocirc;i cuốn, kh&eacute;o l&eacute;o lồng gh&eacute;p c&aacute;c sự kiện lịch sử trong mạch truyện. T&aacute;c giả Thục Linh đ&atilde; viết n&ecirc;n những chương truyện đầy hấp dẫn v&agrave; kh&oacute; đo&aacute;n được c&aacute;i kết cho tới tận những trang cuối c&ugrave;ng.</p>\r\n', 1, 35),
(78, 'Mật Mã Thành Công', '101', '79000', 31, '1715073660_01.jpeg.webp', '<p>T&aacute;c giả: Charles F.Haanel</p>\r\n\r\n<p>Thương Hiệu: SBOOKS</p>\r\n\r\n<p>Nh&agrave; xuất bản: NXB Văn Học</p>\r\n\r\n<p>Năm xuất bản: 2024</p>\r\n\r\n<p>Loại b&igrave;a: B&igrave;a mềm</p>\r\n\r\n<p>Khổ: 13x20 cm</p>\r\n\r\n<p>Số trang: 280</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p><em>Tại sao bạn lại đau khổ trong những cơn trầm cảm?</em></p>\r\n\r\n<p><em>Tại sao bạn lại kh&ocirc;ng thể n&agrave;o cảm nhận được hạnh ph&uacute;c?</em></p>\r\n\r\n<p><em>Tại sao bạn cố gắng rất nhiều nhưng bất hạnh vẫn cứ đeo b&aacute;m lấy bạn?</em></p>\r\n\r\n<p><em>Tại sao bạn bỏ hết vốn liếng nhưng cuộc đời bạn vẫn chẳng c&oacute; g&igrave; ngo&agrave;i con số 0 tr&ograve;n trĩnh?</em></p>\r\n\r\n<p><em>&hellip;</em></p>\r\n\r\n<p>&ldquo;Mật m&atilde; th&agrave;nh c&ocirc;ng&rdquo; kh&ocirc;ng n&oacute;i với bạn rằng bạn phải cười l&ecirc;n th&igrave; mới vui, rằng bạn phải đi theo một quy chuẩn n&agrave;o đ&oacute;. Tất cả những thứ gi&aacute;o điều ấy đ&atilde; v&ocirc; c&ugrave;ng cũ kỹ, lỗi thời v&agrave; kh&ocirc;ng thể dẫn dắt con người được nữa.</p>\r\n\r\n<p>&ldquo;Mật m&atilde; th&agrave;nh c&ocirc;ng&rdquo; sẽ khai mở cho bạn một con đường mới, đưa cho bạn chiếc ch&igrave;a kh&oacute;a qu&yacute; gi&aacute; để mở ra c&aacute;nh cửa của Kh&ocirc;ng gian S&aacute;ng tạo, Tr&iacute; tuệ V&ocirc; hạn v&agrave; Tự do Vĩnh cửu - đ&oacute; l&agrave; chiếc ch&igrave;a kh&oacute;a &ldquo;Tư Duy&rdquo;.&nbsp;</p>\r\n\r\n<p>Cuvier nh&igrave;n thấy một chiếc răng thuộc về một lo&agrave;i động vật đ&atilde; tuyệt chủng. Chiếc răng n&agrave;y muốn c&oacute; một cơ thể để thực hiện chức năng của n&oacute; v&agrave; n&oacute; x&aacute;c định cơ thể đặc biệt m&agrave; n&oacute; cần với độ ch&iacute;nh x&aacute;c đến mức Cuvier c&oacute; thể t&aacute;i tạo lại khung của con vật n&agrave;y.</p>\r\n\r\n<p>Người ta quan s&aacute;t thấy sự nhiễu loạn trong chuyển động của Sao Thi&ecirc;n Vương. Adams v&agrave; Leverrier cần một ng&ocirc;i sao kh&aacute;c ở một nơi nhất định để giữ trật tự cho hệ mặt trời, v&agrave; sao Hải Vương xuất hiện ở địa điểm v&agrave; thời gian x&aacute;c định.</p>\r\n\r\n<p>Mong muốn bản năng của động vật v&agrave; mong muốn tr&iacute; tuệ của Cuvier; mong muốn của thi&ecirc;n nhi&ecirc;n v&agrave; t&acirc;m tr&iacute; của Leverrier v&agrave; Adams giống nhau, v&agrave; do đ&oacute; kết quả l&agrave; giống nhau; ở đ&acirc;y những tư duy về một sự tồn tại dẫn đến c&oacute; một sự tồn tại.&nbsp;</p>\r\n\r\n<p>Để c&oacute; được sức sống, tư tưởng phải được thấm nhuần t&igrave;nh y&ecirc;u. T&igrave;nh y&ecirc;u l&agrave; sản phẩm của cảm x&uacute;c. Do đ&oacute;, điều cần thiết l&agrave; cảm x&uacute;c phải được kiểm so&aacute;t v&agrave; hướng dẫn bởi tr&iacute; tuệ v&agrave; l&yacute; tr&iacute;.</p>\r\n\r\n<p><em>&ldquo;H&atilde;y tư duy thực sự, v&agrave; tư duy của bạn</em></p>\r\n\r\n<p><em>Sẽ c&oacute; nạn đ&oacute;i tr&ecirc;n thế giới;</em></p>\r\n\r\n<p><em>H&atilde;y n&oacute;i thật l&ograve;ng v&agrave; mỗi lời n&oacute;i của bạn</em></p>\r\n\r\n<p><em>Sẽ l&agrave; một hạt giống sinh s&ocirc;i;</em></p>\r\n\r\n<p><em>H&atilde;y sống thực sự v&agrave; cuộc sống của bạn sẽ l&agrave;</em></p>\r\n\r\n<p><em>Một t&iacute;n ngưỡng vĩ đại v&agrave; cao qu&yacute;.&rdquo;</em></p>\r\n\r\n<p>&ndash; Horatio Bonar &ndash;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 46),
(79, 'Lý Thuyết Trò Chơi', '103', '125000', 36, '1715076115_13.jpg.webp', '<p>Cung cấp c&ocirc;ng thức chiến thắng trong c&aacute;c tr&ograve; chơi đấu tr&iacute; h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>Ph&acirc;n t&iacute;ch v&agrave; giải th&iacute;ch c&aacute;c kh&aacute;i niệm l&yacute; thuyết tr&ograve; chơi dễ hiểu.</p>\r\n\r\n<p>Hướng dẫn đọc hiểu h&agrave;nh vi t&acirc;m l&yacute; của người kh&aacute;c v&agrave; chiếm ưu thế trong tr&ograve; chơi.</p>\r\n', '<p>Đời người giống như tr&ograve; chơi, mỗi bước đều phải c&acirc;n nhắc xem đi như thế n&agrave;o, đi về đ&acirc;u, phải kết hợp nhiều yếu tố lại ch&uacute;ng ta mới c&oacute; thể đưa ra được lựa chọn. M&agrave; trong qu&aacute; tr&igrave;nh chọn lựa n&agrave;y c&aacute;c yếu tố khiến ta phải c&acirc;n nhắc v&agrave; những đường đi kh&aacute;c nhau sẽ ảnh hưởng trực tiếp đến kết quả.</p>\r\n\r\n<p>Cuốn s&aacute;ch L&yacute; thuyết tr&ograve; chơi l&agrave; b&aacute;ch khoa to&agrave;n thư về t&acirc;m l&yacute; học, về tẩy n&atilde;o v&agrave; chống lại tẩy n&atilde;o, thao t&uacute;ng v&agrave; chống lại thao t&uacute;ng, thống trị v&agrave; chống lại thống trị. Cuốn s&aacute;ch giới thiệu c&ocirc;ng thức chiến thắng cho những &ldquo;tr&ograve; chơi&rdquo; đấu tr&iacute; giữa người với người trong cuộc sống hằng ng&agrave;y; ph&acirc;n t&aacute;ch c&aacute;c kh&aacute;i niệm l&yacute; thuyết tr&ograve; chơi vốn mơ hồ trở th&agrave;nh ng&ocirc;n ngữ dễ hiểu v&agrave; kết nối liền mạch với nghệ thuật t&acirc;m l&yacute; học; cho ph&eacute;p bạn nắm vững những b&iacute; ẩn của tr&ograve; chơi t&acirc;m l&yacute; trong thời gian ngắn nhất.</p>\r\n\r\n<p>Những kỹ năng trong L&yacute; thuyết tr&ograve; chơi c&oacute; thể gi&uacute;p ch&uacute;ng ta đọc thấu hoạt động t&acirc;m l&yacute; người kh&aacute;c, v&agrave; từ đ&oacute; chiếm thế chủ động trong tr&ograve; chơi đấu tr&iacute; giữa những người xung quanh.</p>\r\n\r\n<p>Những tr&iacute;ch dẫn hay:<br />\r\n- Nếu coi một v&aacute;n chơi như một tr&ograve; chơi, m&agrave; tr&ograve; chơi lu&ocirc;n lu&ocirc;n c&oacute; thắng thua. Một b&ecirc;n thắng th&igrave; đồng nghĩa b&ecirc;n kia thất bại.&nbsp;<br />\r\n- Đằng sau những người chiến thắng lẫy lừng đều che giấu một nỗi khổ t&acirc;m v&agrave; chua x&oacute;t của kẻ thua cuộc.<br />\r\n- H&atilde;y coi x&atilde; hội n&agrave;y như một v&aacute;n cờ, m&agrave; mỗi ch&uacute;ng ta đều l&agrave; những kỳ thủ. Từng đường đi nước bước của ta đều tương ứng với việc đặt từng con cờ. Một kỳ thủ tinh tường cẩn thận sẽ kh&ocirc;ng hấp tấp đ&aacute;nh cờ, họ thường th&ocirc;ng qua việc suy đo&aacute;n lẫn nhau, thậm ch&iacute; t&iacute;nh kế để từng bước đi đều khống chế đối phương cho đến khi gi&agrave;nh được thắng lợi cuối c&ugrave;ng. M&agrave; thuyết tr&ograve; chơi ch&iacute;nh l&agrave; cuốn s&aacute;ch gi&aacute;o khoa dạy những kỳ thủ ch&uacute;ng ta n&ecirc;n đ&aacute;nh cờ như thế n&agrave;o.</p>\r\n', 1, 46),
(80, 'How Psychology Works', '102', '269350', 41, '1715076299_14.jpg.webp', '<p>Giải th&iacute;ch s&uacute;c t&iacute;ch v&agrave; dễ hiểu về c&aacute;c vấn đề t&acirc;m l&yacute;.</p>\r\n\r\n<p>Cung cấp kiến thức về học thuyết t&acirc;m l&yacute; v&agrave; phương ph&aacute;p trị liệu.</p>\r\n\r\n<p>Thiết kế v&agrave; minh họa đẹp mắt, l&agrave;m cho việc đọc th&uacute; vị hơn.</p>\r\n', '<p>MỘT TRONG NHỮNG CUỐN S&Aacute;CH MỞ KH&Oacute;A HỮU &Iacute;CH NHẤT VỀ TƯ DUY, K&Yacute; ỨC V&Agrave; CẢM X&Uacute;C CỦA CON NGƯỜI!</p>\r\n\r\n<p>&Aacute;m sợ l&agrave; g&igrave;, &aacute;m sợ c&oacute; thực sự đ&aacute;ng sợ kh&ocirc;ng? Rối loạn t&acirc;m l&yacute; l&agrave; g&igrave;, l&agrave;m thế n&agrave;o để tho&aacute;t khỏi t&igrave;nh trạng suy nhược v&agrave; x&aacute;o trộn đ&oacute;? Trầm cảm l&agrave; g&igrave;, v&igrave; sao con người hiện đại thường xuy&ecirc;n gặp v&agrave; chống chọi với t&igrave;nh trạng u uất, mệt mỏi v&agrave; tuyệt vọng n&agrave;y?<br />\r\n<br />\r\nT&igrave;m hiểu về c&aacute;c vấn đề t&acirc;m tr&iacute; của con người lu&ocirc;n đầy sức hấp dẫn v&agrave; l&ocirc;i cuốn, v&igrave; vậy m&agrave; t&acirc;m l&yacute; học ra đời, h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển rất nhiều c&aacute;c học thuyết v&agrave; trường ph&aacute;i. Cuốn s&aacute;ch n&agrave;y dẫn dắt bạn đọc qua h&agrave;nh tr&igrave;nh t&igrave;m hiểu c&aacute;c học thuyết v&agrave; trường ph&aacute;i đ&oacute;, về c&aacute;ch c&aacute;c nh&agrave; t&acirc;m l&yacute; diễn giải h&agrave;nh xử v&agrave; t&acirc;m tr&iacute; con người. Tại sao ch&uacute;ng ta c&oacute; những h&agrave;nh vi, suy nghĩ v&agrave; cảm x&uacute;c như vậy, ch&uacute;ng diễn ra v&agrave; kết th&uacute;c như thế n&agrave;o, ch&uacute;ng ảnh hưởng l&acirc;u d&agrave;i, gi&aacute;n đoạn hay ngắn ngủỉ đến đời sống của ch&uacute;ng ta ra sao, l&agrave;m thế n&agrave;o để ch&uacute;ng ta tho&aacute;t khỏi những t&aacute;c động ti&ecirc;u cực của ch&uacute;ng?<br />\r\n<br />\r\nCuốn s&aacute;ch c&oacute; cấu tr&uacute;c khoa học, tr&igrave;nh b&agrave;y dễ hiểu, s&uacute;c t&iacute;ch, thiết kế v&agrave; minh họa đẹp mắt n&agrave;y sẽ mang đến cho bạn những hiểu biết về c&aacute;c học thuyết t&acirc;m l&yacute; v&agrave; c&aacute;c phương ph&aacute;p trị liệu, từ c&aacute;c vấn đề c&aacute; nh&acirc;n đến những ứng dụng thực tế.</p>\r\n', 1, 46),
(81, 'Tôi Tự Học', '104', '95900', 23, '1715076524_15.jpg.webp', '<p>Nhấn mạnh gi&aacute; trị thực sự của học vấn, kh&ocirc;ng chỉ l&agrave; c&oacute; bằng cấp.</p>\r\n\r\n<p>Đề cao việc hiểu v&agrave; mở rộng tri thức, kh&ocirc;ng chỉ đơn thuần thu thập th&ocirc;ng tin.</p>\r\n\r\n<p>Cung cấp phương ph&aacute;p học tập hiệu quả v&agrave; định hướng cho việc tự học.</p>\r\n', '<p>S&aacute;ch&nbsp;<strong>&ldquo;T&ocirc;i tự học&rdquo;</strong>&nbsp;của t&aacute;c giả Nguyễn Duy Cần đề cập đến kh&aacute;i niệm, mục đ&iacute;ch của học vấn đối với con người đồng thời n&ecirc;u l&ecirc;n một số phương ph&aacute;p học tập đ&uacute;ng đắn v&agrave; hiệu quả. T&aacute;c giả cho rằng gi&aacute; trị của học vấn nằm ở sự lĩnh hội v&agrave; mở mang tri thức của con người chứ kh&ocirc;ng đơn thuần thể hiện tr&ecirc;n bằng cấp. Trong x&atilde; hội ng&agrave;y nay, kh&ocirc;ng &iacute;t người qu&ecirc;n đi &yacute; nghĩa đ&iacute;ch thực của học vấn, biến việc học của m&igrave;nh th&agrave;nh c&ocirc;ng cụ để kiếm tiền nhưng thực ra n&oacute; chỉ l&agrave; phương tiện để đưa con người đến th&agrave;nh c&ocirc;ng m&agrave; th&ocirc;i. Bởi v&igrave; học kh&ocirc;ng phải để lấy bằng m&agrave; học c&ograve;n để &ldquo;biết m&igrave;nh&rdquo; v&agrave; để &ldquo;đối nh&acirc;n xử thế&rdquo;.</p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y tuy đ&atilde; được xuất bản từ rất l&acirc;u nhưng gi&aacute; trị của s&aacute;ch vẫn c&ograve;n nguy&ecirc;n vẹn. Những tư tưởng, chủ đề của s&aacute;ch vẫn ph&ugrave; hợp v&agrave; c&oacute; thể &aacute;p dụng trong đời sống hiện nay. Thiết nghĩ, cuốn s&aacute;ch n&agrave;y rất cần cho mọi đối tượng bạn đọc v&igrave; kh&ocirc;ng c&oacute; giới hạn n&agrave;o cho việc truy tầm kiến thức, việc học l&agrave; sự nghiệp l&acirc;u d&agrave;i của mỗi con người. Đặc biệt, cuốn s&aacute;ch l&agrave; một t&agrave;i liệu qu&yacute; để c&aacute;c bạn học sinh &ndash; sinh vi&ecirc;n tham khảo, tổ chức lại việc học của m&igrave;nh một c&aacute;ch hợp l&yacute; v&agrave; khoa học. C&aacute;c bậc phụ huynh cũng cần tham khảo s&aacute;ch n&agrave;y để định hướng v&agrave; tư vấn cho con em m&igrave;nh trong qu&aacute; tr&igrave;nh học tập.</p>\r\n', 1, 46),
(82, 'Thuật Yêu Đương', '105', '101400', 19, '1715076708_16.jpg.webp', '<p>T&aacute;c phẩm mang đến c&aacute;i nh&igrave;n s&acirc;u sắc về t&igrave;nh y&ecirc;u từ người trải nghiệm.</p>\r\n\r\n<p>G&oacute;c nh&igrave;n mới v&agrave; độc đ&aacute;o về t&igrave;nh y&ecirc;u từ người đ&atilde; trải qua nhiều biến động.</p>\r\n\r\n<p>Xuất bản bởi NXB Trẻ, c&ocirc;ng ty ph&aacute;t h&agrave;nh uy t&iacute;n.</p>\r\n', '<p><strong>Thuật Y&ecirc;u Đương</strong>&nbsp;l&agrave; một trong nhiều t&aacute;c phẩm nổi tiếng của &ocirc;ng, ra mắt bạn đọc v&agrave;o năm 1961, khi đ&oacute; cụ Nguyễn Duy Cần tr&ecirc;n 50 tuổi v&agrave; đ&atilde; c&oacute; một gia t&agrave;i s&aacute;ch kh&aacute; đồ sộ. Th&ocirc;ng thường c&aacute;c nh&agrave; văn đều c&oacute; những s&aacute;ng t&aacute;c hay về t&igrave;nh y&ecirc;u trong lứa tuổi thanh xu&acirc;n, v&igrave; sao một học giả nổi tiếng như Nguyễn Duy Cần lại viết v&agrave; xuất bản một cuốn s&aacute;ch về t&igrave;nh y&ecirc;u khi tuổi đ&atilde; xế chiều, v&agrave; v&igrave; sao &ocirc;ng lại chọn t&igrave;nh y&ecirc;u l&agrave;m chủ đề cho một cuốn s&aacute;ch của m&igrave;nh?</p>\r\n\r\n<p>C&acirc;u trả lời giản dị l&agrave;: Chỉ c&oacute; thời gian mới l&agrave;m cho con người hiểu được sự s&acirc;u sắc của t&igrave;nh y&ecirc;u.</p>\r\n\r\n<p>Đi s&acirc;u v&agrave;o t&aacute;c phẩm&nbsp;Thuật Y&ecirc;u Đương,&nbsp;ta nhận ra được c&aacute;ch t&aacute;c giả tiếp cận với đề t&agrave;i t&igrave;nh y&ecirc;u ho&agrave;n to&agrave;n mới mẻ. Đ&oacute; l&agrave; c&aacute;i nh&igrave;n của một con người đ&atilde; sống hơn nửa đời m&igrave;nh với biết bao thăng trầm, biến động của x&atilde; hội, đ&atilde; trải nghiệm hết mọi cung bậc x&uacute;c cảm buồn giận thương vui trong đời.</p>\r\n', 1, 37),
(83, 'Flow - Dòng chảy', '106', '177840', 37, '1715077092_17.jpg.webp', '<p>Giới thiệu kh&aacute;i niệm &quot;d&ograve;ng chảy&quot; v&agrave; cung cấp c&ocirc;ng cụ khoa học.</p>\r\n\r\n<p>Tạo chuyến du h&agrave;nh qua l&atilde;nh địa t&acirc;m tr&iacute;.</p>\r\n\r\n<p>Sử dụng ng&ocirc;n ngữ th&acirc;n thiện, gần gũi v&agrave; dễ tiếp cận.</p>\r\n', '<p><strong>D&agrave;nh cho những ai muốn tối ưu khả năng tập trung để t&igrave;m kiếm sự gắn kết trong c&ocirc;ng việc, hướng đến sự vi&ecirc;n m&atilde;n, v&agrave; hạnh ph&uacute;c trong mọi trải nghiệm cuộc sống</strong></p>\r\n\r\n<p>C&oacute; bao giờ bạn ho&agrave;n to&agrave;n ch&igrave;m đắm v&agrave;o một cuốn s&aacute;ch hay, một c&ocirc;ng việc, hay một buổi tr&igrave;nh diễn đến mức kh&ocirc;ng nhận thấy h&agrave;ng giờ đồng hồ đ&atilde; tr&ocirc;i qua, thậm ch&iacute; bạn chẳng c&oacute; &yacute; niệm g&igrave; về mọi thứ xung quanh hay sự tồn tại của ch&iacute;nh m&igrave;nh? Thời khắc ấy, một sự khoan kho&aacute;i kỳ lạ tu&ocirc;n chảy trong con người bạn, đến mức bạn kh&ocirc;ng ngừng t&igrave;m kiếm để c&oacute; lại những cảm gi&aacute;c tương tự, nhưng điều đ&oacute; dường như l&agrave; kh&ocirc;ng thể, bởi bạn chưa thật sự hiểu cảm gi&aacute;c đ&oacute; l&agrave; g&igrave;, cũng như nguồn gốc h&igrave;nh th&agrave;nh n&ecirc;n trải nghiệm tuyệt vời ấy.</p>\r\n\r\n<p>C&acirc;u trả lời sẽ được t&igrave;m thấy trong &ldquo;<strong><em>D&ograve;ng chảy&rdquo; (Flow),</em></strong>&nbsp;cuốn s&aacute;ch tổng hợp c&ocirc;ng tr&igrave;nh trọn đời của nh&agrave; t&acirc;m l&yacute; học người Mỹ gốc Hungary Mihaly Csikszentmihalyi - c&acirc;y đại thụ của ng&agrave;nh T&acirc;m l&yacute; học đương đại. Trạng th&aacute;i d&ograve;ng chảy (Flow) l&agrave; trạng th&aacute;i &yacute; thức đạt trật tự h&agrave;i h&ograve;a, &ldquo;<em>trong đ&oacute; con người tham gia v&agrave;o một hoạt động s&acirc;u sắc đến mức dường như chẳng c&ograve;n điều g&igrave; kh&aacute;c l&agrave; quan trọng</em>&rdquo;, người ta sẽ quyết t&acirc;m thực hiện hoạt động chỉ bởi lợi &iacute;ch tự th&acirc;n khi l&agrave;m việc đ&oacute;.</p>\r\n\r\n<p>Hai kh&aacute;i niệm kh&ocirc;ng thể t&aacute;ch rời trong thuyết D&ograve;ng chảy của Mihaly Csikszentmihalyi l&agrave; &ldquo;<em>trạng th&aacute;i d&ograve;ng chảy</em>&rdquo; v&agrave; &ldquo;<em>trải nghiệm tối ưu</em>&rdquo;, những tiền đề của một cuộc sống hạnh ph&uacute;c. Khi trạng th&aacute;i d&ograve;ng chảy diễn ra trong t&acirc;m tr&iacute;, con người c&oacute; được trải nghiệm tối ưu - &ldquo;<em>cơ thể hay t&acirc;m tr&iacute; của một người được k&eacute;o căng đến giới hạn của n&oacute;, trong một nỗ lực tự nguyện, nhằm ho&agrave;n th&agrave;nh một nhiệm vụ kh&oacute; khăn v&agrave; đ&aacute;ng gi&aacute;&rdquo;</em>&nbsp;- ch&iacute;nh l&agrave; l&uacute;c bạn ho&agrave;n to&agrave;n kiểm so&aacute;t chất lượng cuộc sống, bất chấp c&aacute;c điều kiện ngoại cảnh.</p>\r\n\r\n<p>Sau Thế chiến thứ 2, Mihaly Csikszentmihalyi nhận thấy nhiều người đ&atilde; kh&ocirc;ng thể tận hưởng cuộc sống vi&ecirc;n m&atilde;n v&igrave; những hệ lụy của chiến tranh. Điều n&agrave;y trở th&agrave;nh động lực th&ocirc;i th&uacute;c Mihaly đ&agrave;o s&acirc;u, t&igrave;m hiểu về bản chất của hạnh ph&uacute;c, v&agrave; t&igrave;m lời giải cho c&acirc;u hỏi &ldquo;<em>điều g&igrave; tạo n&ecirc;n một cuộc đời đ&aacute;ng sống?</em>&rdquo;. Mihaly đ&atilde; d&agrave;nh gần như to&agrave;n bộ cuộc đời của m&igrave;nh cho những nghi&ecirc;n cứu xoay quanh chủ đề n&agrave;y, v&agrave; cuốn s&aacute;ch&nbsp;<a href=\"https://firstnews.com.vn/vi/tac-pham/flow-dong-chay-p1476.html\" target=\"_blank\"><strong><em>D&ograve;ng chảy</em></strong></a>&nbsp;ch&iacute;nh l&agrave; th&agrave;nh quả vĩ đại từ c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu đ&oacute;. T&aacute;c phẩm kh&ocirc;ng những tạo n&ecirc;n t&ecirc;n tuổi của &ocirc;ng, m&agrave; c&ograve;n trở th&agrave;nh quyển s&aacute;ch kinh điển, đ&oacute;ng g&oacute;p to lớn trong việc gi&uacute;p nhiều người đạt được trải nghiệm thăng hoa nhất trong cuộc sống.</p>\r\n\r\n<p>Thuyết D&ograve;ng chảy kh&ocirc;ng chỉ l&agrave; một chủ đề học thuật đơn thuần, m&agrave; n&oacute; c&ograve;n mang t&iacute;nh thực tiễn v&agrave; ứng dụng cao. Chẳng hạn như trong chủ đề n&acirc;ng cao hiệu suất trong c&ocirc;ng việc, c&oacute; rất nhiều đầu s&aacute;ch nổi tiếng đ&atilde; được ph&aacute;t triển dựa tr&ecirc;n thuyết D&ograve;ng chảy của Mihaly Csikszentmihalyi, như &ldquo;<em>Deep Work</em>&rdquo; của Cal Newport, hay &ldquo;<em>Thinking, Fast and Slow</em>&rdquo; của Daniel Kahneman.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, kh&ocirc;ng chỉ dừng lại ở đ&oacute;, thuyết D&ograve;ng chảy hiện đang được tiếp tục nghi&ecirc;n cứu v&agrave; &aacute;p dụng trong hầu hết c&aacute;c ng&agrave;nh Khoa học X&atilde; hội, trong Kinh tế, thậm ch&iacute; trong nghi&ecirc;n cứu sự tiến h&oacute;a của nh&acirc;n loại, v&agrave; c&aacute;c lĩnh vực đời sống kh&aacute;c nhau nhằm cải thiện chất lượng cuộc sống, bao gồm cả t&acirc;m l&yacute; trị liệu, cải tạo tội phạm vị th&agrave;nh ni&ecirc;n, gi&aacute;o dục, &acirc;m nhạc... V&agrave; dẫu tr&igrave;nh b&agrave;y một vấn đề học thuật, cuốn s&aacute;ch n&agrave;y được t&aacute;c giả viết d&agrave;nh cho độc giả phổ th&ocirc;ng, nhằm t&oacute;m lược c&aacute;c nghi&ecirc;n cứu trong nhiều thập kỷ về những kh&iacute;a cạnh t&iacute;ch cực trong trải nghiệm của con người.</p>\r\n\r\n<p>Thuyết D&ograve;ng chảy được x&acirc;y dựng tr&ecirc;n cơ sở giải phẫu &yacute; thức, truy t&igrave;m nguồn gốc sự bất m&atilde;n của con người, trong đ&oacute; thế lực ảnh hưởng xấu nhất đến &yacute; thức ch&iacute;nh l&agrave;&nbsp;<em>entropy t&acirc;m thần</em>&nbsp;- trạng th&aacute;i rối loạn thường thấy ở tất cả mọi người. Dựa tr&ecirc;n cơ chế thiết lập&nbsp;<em>trật tự trong &yacute; thức</em>, t&igrave;m kiếm&nbsp;<em>sự thưởng thức</em>&nbsp;v&agrave; c&aacute;c&nbsp;<em>trải nghiệm c&oacute; mục đ&iacute;ch tự th&acirc;n</em>, trạng th&aacute;i d&ograve;ng chảy l&agrave; thứ ch&uacute;ng ta chủ động l&agrave;m cho n&oacute; xảy ra.</p>\r\n', 1, 46),
(84, 'Power Of Now', '107', '126700', 33, '1715077283_18.jpg.webp', '<p>S&aacute;ch viết s&uacute;c t&iacute;ch, th&acirc;m thu&yacute;, gi&uacute;p bạn chi&ecirc;m nghiệm s&acirc;u sắc.</p>\r\n\r\n<p>Ng&ocirc;n ngữ đương đại, dễ tiếp cận v&agrave; đi v&agrave;o l&ograve;ng độc giả.</p>\r\n\r\n<p>Gi&uacute;p bạn vượt qua kh&oacute; khăn, x&acirc;y dựng lại mối quan hệ tốt đẹp.</p>\r\n', '<p><strong>Sức Mạnh Của Hiện Tại</strong>&nbsp;được viết một c&aacute;ch s&uacute;c t&iacute;ch v&agrave; th&acirc;m thu&yacute; n&ecirc;n nhiều khi ta chỉ cần đọc một c&acirc;y hay một đoạn ngắn th&igrave; đ&atilde; cảm thấy vừa đủ, v&igrave; quả thực ch&uacute;ng ta cần c&oacute; kh&ocirc;ng gian để chi&ecirc;m nghiệm những điều s&acirc;u sắc m&agrave; t&aacute;c giả đ&atilde; đưa ra. Nhờ sử dụng ng&ocirc;n ngữ đương đại n&ecirc;n &quot;Sức Mạnh Của Hiện Tại&quot; dễ d&agrave;ng đi v&agrave;o l&ograve;ng độc giả. Cuốn s&aacute;ch n&agrave;y c&oacute; năng lực rất lớn để gi&uacute;p bạn chuyển ho&aacute; khổ đau v&agrave; đưa bạn đến giải tho&aacute;t.</p>\r\n\r\n<p>&quot;Sức Mạnh Của Hiện Tại&quot; sẽ rất hữu &iacute;ch v&agrave; thực dụng nếu bạn đang c&oacute; những kh&oacute; khăn trong đời sống c&aacute; nh&acirc;n, hay đời sống vợ chồng, cuốn s&aacute;ch n&agrave;y sẽ gi&uacute;p bạn vượt qua những kh&oacute; khăn đ&oacute;, x&acirc;y dựng lại mối quan hệ ng&agrave;y một tốt đẹp hơn. Thậm ch&iacute; quyển s&aacute;ch n&agrave;y c&ograve;n c&oacute; khả năng gi&uacute;p bạn thực hiện những ước mơ, ho&agrave;i b&atilde;o s&acirc;u k&iacute;n ở trong bạn bấy nay. V&agrave; xa hơn nữa, &quot;Sức mạnh của hiện tại&quot; c&oacute; thể gi&uacute;p bạn thực hiện được mục đ&iacute;ch tối hậu của một con người l&agrave; nhận thức được bản chất ch&acirc;n thực của m&igrave;nh l&agrave; g&igrave;.</p>\r\n\r\n<p>Sức mạnh của hiện tại - The Power of now - t&aacute;c phẩm nổi tiếng thế giới, được thời b&aacute;o New York Times b&igrave;nh chọn l&agrave; cuốn s&aacute;ch hay v&agrave; b&aacute;n chạy nhất, thực sự thiết thực v&agrave; hữu &iacute;ch cho cuộc sống tinh thần mọi người trong thời đại mới. Ch&acirc;n l&yacute; m&agrave; bậc thầy về t&acirc;m linh - Eckhart Tolle, truyền tải trong t&aacute;c phẩm đặc biệt n&agrave;y ch&iacute;nh l&agrave; ph&acirc;n t&iacute;ch được nỗi đau khổ tinh thần của con người xảy ra triền mi&ecirc;n khi lu&ocirc;n ch&igrave;m đắm suy nghĩ v&agrave;o qu&aacute; khứ v&agrave; kh&ocirc;ng nhận thức được gi&aacute; trị v&agrave; sức mạnh của Hiện tại. Khi tập trung v&agrave;o hiện tại, bạn sẽ vượt l&ecirc;n tất cả những suy nghĩ ti&ecirc;u cực, t&igrave;m được bản ng&atilde; của m&igrave;nh v&agrave; tinh thần của bạn sẽ thanh thản hơn rất nhiều.</p>\r\n\r\n<p>Cuốn s&aacute;ch l&agrave; người đồng h&agrave;nh gi&uacute;p bạn t&igrave;m về với ch&iacute;nh m&igrave;nh. Bạn sẽ bất ngờ kh&aacute;m ph&aacute; ra những ch&acirc;n gi&aacute; trị qu&yacute; b&aacute;u m&agrave; ch&iacute;nh bạn đang c&oacute; sẵn nhưng bạn đ&atilde; kh&ocirc;ng nhận ra. Cuốn s&aacute;ch l&agrave; kim chỉ nam cho tất cả ch&uacute;ng ta t&igrave;m lại gi&aacute; trị đ&iacute;ch thực của cuộc sống v&agrave; cảm nhận những gi&aacute; trị tươi đẹp vốn c&oacute;.</p>\r\n', 1, 46);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`) VALUES
(85, 'Chữa Lành Nỗi Đau', '108', '70500', 50, '1715077785_19.jpg.webp', '<p>Kh&aacute;m ph&aacute; năng lực tiềm ẩn v&agrave; khơi dậy tinh thần mạnh mẽ, lạc quan.</p>\r\n\r\n<p>Cung cấp phương ph&aacute;p v&agrave; lời khuy&ecirc;n trị liệu nỗi đau v&agrave; &aacute;m ảnh ti&ecirc;u cực.</p>\r\n\r\n<p>Gi&uacute;p hiểu v&agrave; y&ecirc;u thương ch&iacute;nh m&igrave;nh, tạo thay đổi t&iacute;ch cực.</p>\r\n', '<p>Cuộc sống h&agrave;ng ng&agrave;y đầy rẫy lo toan, tất bật lu&ocirc;n khiến cho bạn c&oacute; cảm gi&aacute;c như m&igrave;nh đang đối đầu với nhiều &aacute;p lực cuộc sống. Những nỗi đau, m&acirc;u thuẫn trong c&aacute;c mối quan hệ bạn b&egrave;, c&ocirc;ng việc, t&igrave;nh cảm lu&ocirc;n nảy sinh khiến cho bạn cảm thấy ch&aacute;n nản, mệt mỏi v&agrave; kh&ocirc;ng c&ograve;n cảm nhận được &yacute; nghĩa cuộc sống.&nbsp;</p>\r\n\r\n<p>Thời gian bất tận, thế giới v&ocirc; c&ugrave;ng, nhưng suy nghĩ của ch&uacute;ng ta c&ograve;n rộng lớn v&agrave; mạnh mẽ hơn nhiều. Ch&uacute;ng ta c&oacute; thể kiến tạo ra cuộc sống theo &yacute; m&igrave;nh v&igrave; ch&uacute;ng ta sinh ra để trao quyền l&agrave;m việc đ&oacute;. Mỗi người ch&uacute;ng ta l&agrave; một &ocirc;ng ho&agrave;ng, b&agrave; ch&uacute;a cai quản c&aacute;c bộ phận của thể x&aacute;c, hướng dẫn c&aacute;c hoạt động tinh thần, l&agrave;m cho đời sống của m&igrave;nh thịnh vượng hơn l&ecirc;n. Nhưng nhiều người đ&atilde; kh&ocirc;ng &yacute; thức được điều đ&oacute; v&agrave; đang phải chịu đựng một cuộc sống với buồn đau, bất hạnh v&agrave; những vết thương kh&ocirc;ng thể n&agrave;o cứu chữa được.</p>\r\n\r\n<p>Nếu bạn vẫn c&ograve;n băn khoăn chưa biết n&ecirc;n bắt đầu từ đ&acirc;u v&agrave; n&ecirc;n sử dụng phương ph&aacute;p n&agrave;o tốt nhất để vực dậy ch&iacute;nh m&igrave;nh, th&igrave; h&atilde;y thử đọc quyển s&aacute;ch Chữa L&agrave;nh Nỗi Đau của t&aacute;c giả Louise L. Hay. Cuốn s&aacute;ch n&agrave;y sẽ gi&uacute;p bạn khơi dậy những năng lực thực sự đang tiềm ẩn b&ecirc;n trong m&igrave;nh. Sức mạnh đ&oacute; sẽ t&aacute;c động kh&ocirc;ng chỉ đến suy nghĩ v&agrave; cuộc sống của ri&ecirc;ng bạn m&agrave; c&ograve;n ảnh hưởng trực tiếp đến những người m&agrave; bạn tiếp x&uacute;c h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>C&oacute; thể trước đ&acirc;y bạn chưa nhận ra nhưng thật sự l&agrave; bạn đang sở hữu một tinh thần mạnh mẽ, lạc quan đang cần được kh&aacute;m ph&aacute;. Mỗi ph&uacute;t gi&acirc;y trong hiện tại n&agrave;y đ&atilde; chứa đựng đầy đủ những g&igrave; bạn đang t&igrave;m kiếm để trả lời những c&acirc;u hỏi của ri&ecirc;ng m&igrave;nh. Chữa L&agrave;nh Nỗi Đau kh&ocirc;ng những c&oacute; thể gi&uacute;p bạn tho&aacute;t khỏi những &aacute;m ảnh ti&ecirc;u cực l&acirc;u nay trong cuộc sống, m&agrave; quyển s&aacute;ch n&agrave;y c&ograve;n gi&uacute;p bạn biết c&aacute;ch y&ecirc;u thương ch&iacute;nh m&igrave;nh để gieo những hạt giống hy vọng cho tương lai.</p>\r\n\r\n<p>H&atilde;y sống cuộc đời đ&iacute;ch thực của ch&iacute;nh m&igrave;nh, trở th&agrave;nh con người m&agrave; bạn mong muốn, vươn đến những ước mơ vẫn hằng ấp ủ v&agrave; hướng tới những điều tốt đẹp trong mối quan hệ với người xung quanh. Khi đ&oacute; bạn sẽ truyền nguồn cảm hướng cho rất nhiều người kh&aacute;c.</p>\r\n', 1, 46),
(86, 'Kinh Doanh Bảo Hiểm', '109', '52000', 38, '1715077927_20.jpg.webp', '', '<p>Luật Kinh Doanh Bảo Hiểm số 08/2022/Qh15 đ&atilde; được Quốc Hội th&ocirc;ng qua ng&agrave;y 16 th&aacute;ng 6 năm 2022 (&nbsp; Luật n&agrave;y c&oacute; hiệu lực từ ng&agrave;y 01 -01 -2023)</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. B&ecirc;n cạnh đ&oacute;, tuỳ v&agrave;o loại sản phẩm, h&igrave;nh thức v&agrave; địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, thuế nhập khẩu (đối với đơn h&agrave;ng giao từ nước ngo&agrave;i c&oacute; gi&aacute; trị tr&ecirc;n 1 triệu đồng)</p>\r\n', 1, 45),
(87, 'Luật Doanh Nghiệp', '110', '70000', 27, '1715078206_21.jpg.webp', '', '<p>Luật Doanh nghiệp được Quốc hội kh&oacute;a XIV th&ocirc;ng qua ng&agrave;y 17/6/2020 quy định về việc th&agrave;nh lập, tổ chức quản l&yacute;, tổ chức lại, giải thể v&agrave; hoạt động c&oacute; li&ecirc;n quan của doanh nghiệp, bao gồm c&ocirc;ng ty tr&aacute;ch nhiệm hữu hạn, c&ocirc;ng ty cổ phần, c&ocirc;ng ty hợp danh v&agrave; doanh nghiệp tư nh&acirc;n; quy định về nh&oacute;m c&ocirc;ng ty. Luật Doanh nghiệp năm 2020 thay thế cho Luật Doanh nghiệp năm 2014.</p>\r\n\r\n<p>Ng&agrave;y 11/01/2022, tại kỳ họp bất thường lần thứ nhất, Quốc hội kh&oacute;a XV đ&atilde; th&ocirc;ng qua Luật sửa đổi, bổ sung một số điều của Luật Đầu tư c&ocirc;ng, Luật Đầu tư theo phương thức đối t&aacute;c c&ocirc;ng tư, Luật Đầu tư, Luật Nh&agrave; ở, Luật Đấu thầu, Luật Điện lực, Luật Doanh nghiệp, Luật Thuế ti&ecirc;u thụ đặc biệt v&agrave; Luật Thi h&agrave;nh &aacute;n d&acirc;n sự, trong đ&oacute; c&oacute; nội dung sửa đổi, bổ sung một số điều của Luật Doanh nghiệp năm 2020.</p>\r\n', 1, 45),
(88, 'Luật Phá Sản', '111', '43000', 61, '1715078368_22.jpg.webp', '', '<p>Luật ph&aacute; sản được Quốc hội nước Cộng h&ograve;a x&atilde; hội chủ nghĩa Việt Nam kh&oacute;a XIII, kỳ họp thứ 7 th&ocirc;ng qua ng&agrave;y 19/6/2014, c&oacute; hiệu lực thi h&agrave;nh từ ng&agrave;y 01/01/2015.</p>\r\n\r\n<p>Luật gồm 14 chương, 133 điều, quy định về tr&igrave;nh tự, thủ tục nộp đơn, thụ l&yacute; v&agrave; mở thủ tục ph&aacute; sản; x&aacute;c định nghĩa vụ về t&agrave;i sản v&agrave; biện ph&aacute;p bảo to&agrave;n t&agrave;i sản trong qu&aacute; tr&igrave;nh giải quyết ph&aacute; sản; thủ tục phục hồi hoạt động kinh doanh; tuy&ecirc;n bố ph&aacute; sản v&agrave; thi h&agrave;nh quyết định tuy&ecirc;n bố ph&aacute; sản.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 45),
(89, 'Sở Hữu Trí Tuệ', '112', '75000', 44, '1715078530_23.jpg.webp', '', '<p>Luật sở hữu tr&iacute; tuệ (hiện h&agrave;nh)&nbsp;(sửa đổi, bổ sung năm 2009, 2019)&nbsp;sửa đổi c&aacute;c luật:&nbsp;Luật Sở hữu tr&iacute; tuệ số 50/2005/QH11&nbsp;đ&atilde; được sửa đổi, bổ sung một số điều theo&nbsp;Luật số 36/2009/QH12.</p>\r\n\r\n<p>Luật&nbsp;c&oacute; hiệu lực thi h&agrave;nh từ ng&agrave;y&nbsp;01/11/2019.&nbsp;Luật n&agrave;y bổ sung&nbsp;mục 32a v&agrave;o sau mục 32 Phụ lục 4 Danh mục ng&agrave;nh, nghề đầu tư kinh doanh c&oacute; điều kiện của&nbsp;Luật Đầu tư số 67/2014/QH13&nbsp;đ&atilde; được sửa đổi, bổ sung một số điều theo&nbsp;Luật số&nbsp;90/2015/QH13,&nbsp;Luật số 03/2016/QH14, Luật số&nbsp;04/2017/QH14&nbsp;v&agrave; Luật số 28/2018/QH14.</p>\r\n', 1, 45),
(90, 'Luật Chứng Khoán', '113', '75000', 70, '1715078689_24.jpg.webp', '', '<p><em>Luật Chứng kho&aacute;n</em>&nbsp;được Quốc hội kh&oacute;a XIV, kỳ họp thứ 8 th&ocirc;ng qua ng&agrave;y 26/11/2019, c&oacute; hiệu lực thi h&agrave;nh từ ng&agrave;y 01/01/2021. Luật gồm 10 chương, 135 điều, quy định c&aacute;c hoạt động về chứng kho&aacute;n v&agrave; thị trường chứng kho&aacute;n; quyền v&agrave; nghĩa vụ của tổ chức, c&aacute; nh&acirc;n trong lĩnh vực chứng kho&aacute;n; tổ chức thị trường chứng kho&aacute;n; quản l&yacute; nh&agrave; nước về chứng kho&aacute;n v&agrave; thị trường chứng kho&aacute;n.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; văn bản luật cần c&oacute; đối với tất cả c&aacute;c c&aacute; nh&acirc;n, tổ chức li&ecirc;n quan đến hoạt động về chứng kho&aacute;n, như c&aacute;c c&aacute; nh&acirc;n, nh&agrave; đầu tư, tổ chức kinh tế trong v&agrave; ngo&agrave;i nước tham gia thị trường chứng kho&aacute;n, c&aacute;c c&ocirc;ng ty đại ch&uacute;ng, th&agrave;nh vi&ecirc;n của c&aacute;c sở giao dịch chứng kho&aacute;n v&agrave; c&aacute;c c&ocirc;ng ty</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 45),
(91, 'Luật Dân Sự', '114', '92000', 37, '1715099199_25.jpg.webp', '', '<p>Bộ luật n&agrave;y c&oacute; hiệu lực thi h&agrave;nh từ ng&agrave;y 01 th&aacute;ng 01 năm 2017.</p>\r\n\r\n<p>Bộ luật n&agrave;y đ&atilde; được Quốc hội nước Cộng h&ograve;a x&atilde; hội chủ nghĩa Việt Nam kh&oacute;a XIII, kỳ họp thứ 10 th&ocirc;ng qua ng&agrave;y 24 th&aacute;ng 11 năm 2015.</p>\r\n', 1, 45),
(92, 'Luật Hình Sự', '115', '129000', 45, '1715099337_26.jpg.webp', '', '<p>Bộ luật H&igrave;nh sự (hiện h&agrave;nh) (Bộ luật năm 2015, sửa đổi, bổ sung năm 2017) gồm 3 phần với 26 chương v&agrave; 426 điều, quy định về&nbsp;<em>tội phạm</em>&nbsp;v&agrave;&nbsp;<em>h&igrave;nh phạt</em>. Đ&acirc;y l&agrave; văn bản nhất thể h&oacute;a của hai văn bản luật gồm&nbsp;<em>Bộ luật H&igrave;nh sự</em>&nbsp;(Luật số 100/2015/QH13) được Quốc hội kh&oacute;a XIII, kỳ họp thứ 10 th&ocirc;ng qua ng&agrave;y 27/11/2015 v&agrave;&nbsp;<em>Luật sửa đổi, bổ sung một số điều của Bộ luật H&igrave;nh sự&nbsp;</em>số 100/2015/QH13 được Quốc hội kh&oacute;a XIV, kỳ họp thứ 3 th&ocirc;ng qua ng&agrave;y 20/6/2017. Cả hai luật đều c&oacute; hiệu lực thi h&agrave;nh từ ng&agrave;y 01/01/2018.</p>\r\n', 1, 45),
(93, 'Truyện Cổ Tích', '116', '119000', 37, '1715099561_27.jpg.webp', '', '<p>Truyện cổ t&iacute;ch lu&ocirc;n đ&oacute;ng một vai tr&ograve; quan trọng đối với sự h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển nh&acirc;n c&aacute;ch của trẻ. Những c&acirc;u chuyện mang đậm gi&aacute; trị nh&acirc;n đạo, c&oacute; &yacute; nghĩa gi&aacute;o dục s&acirc;u sắc gi&uacute;p c&aacute;c em trưởng th&agrave;nh, ph&aacute;t triển tư duy một c&aacute;ch l&agrave;nh mạnh v&agrave; trong s&aacute;ng nhất. Kh&ocirc;ng những thế, lời văn đẹp đẽ trong c&aacute;c c&acirc;u chuyện cổ c&ograve;n c&oacute; t&aacute;c dụng bồi dưỡng cho trẻ năng lực thẩm mỹ, khả năng cảm thụ văn học, gi&uacute;p trẻ sử dụng ng&ocirc;n ngữ một c&aacute;ch phong ph&uacute; v&agrave; hiệu quả.</p>\r\n\r\n<p><strong>Cổ T&iacute;ch Việt Nam&nbsp;</strong>chọn lọc những c&acirc;u chuyện đặc sắc nhất trong kho t&agrave;ng truyện cổ t&iacute;ch Việt Nam, bao gồm: Tấm C&aacute;m, C&aacute;i ang v&agrave;ng, Ở &aacute;c gặp &aacute;c, Hai c&acirc;y khế, Ch&uacute; b&eacute; th&ocirc;ng minh, Ch&agrave;ng rể c&oacute;c, Vua Lợn, Truyện Thạch Sanh, Lưỡi dao thần, Sơn Tinh Thủy Tinh, N&agrave;ng Ng&oacute;n &Uacute;t&hellip;</p>\r\n', 1, 43),
(94, 'Ngọn Đèn Không Tắt', '117', '50000', 62, '1715099791_28.jpg.webp', '<p>T&aacute;c giả : Nguyễn Ngọc Tư&nbsp;<br />\r\nB&igrave;a cứng loại II<br />\r\nChất liệu giấy tốt , kh&ocirc;ng phai m&agrave;u&nbsp;<br />\r\n&nbsp;</p>\r\n', '<p>Ngọn đ&egrave;n kh&ocirc;ng tắt l&agrave; bức tranh sinh động về thanh ni&ecirc;n h&ocirc;m nay tr&ecirc;n mọi mặt đời sống; l&agrave; tập truyện ngắn vừa hấp dẫn vừa thuyết phục. Với giọng văn mộc mạc b&igrave;nh dị, với ng&ocirc;n ngữ đời thường đ&atilde; tạo n&ecirc;n một kh&ocirc;ng kh&iacute; rất tự nhi&ecirc;n về m&agrave;u sắc, hương vị của mảnh đất cuối c&ugrave;ng tổ quốc. Qua ng&ograve;i b&uacute;t của Nguyễn Ngọc Tư, những con người lam lũ giản dị, bộc trực ấy chứa b&ecirc;n trong cả một t&acirc;m hồn vừa nh&acirc;n hậu vừa tinh tế qua c&aacute;ch đối nh&acirc;n xử thế.</p>\r\n\r\n<p>Gi&aacute; sản phẩm tr&ecirc;n Tiki đ&atilde; bao gồm thuế theo luật hiện h&agrave;nh. B&ecirc;n cạnh đ&oacute;, tuỳ v&agrave;o loại sản phẩm, h&igrave;nh thức v&agrave; địa chỉ giao h&agrave;ng m&agrave; c&oacute; thể ph&aacute;t sinh th&ecirc;m chi ph&iacute; kh&aacute;c như ph&iacute; vận chuyển, phụ ph&iacute; h&agrave;ng cồng kềnh, thuế nhập khẩu (đối với đơn h&agrave;ng giao từ nước ngo&agrave;i c&oacute; gi&aacute; trị tr&ecirc;n 1 triệu đồng).....</p>\r\n', 1, 43),
(95, 'Hành Lý Hư Vô', '118', '70800', 108, '1715099930_29.jpg.webp', '<p>Tập tản văn chứa tấm l&ograve;ng ch&acirc;n th&agrave;nh, đồng cảm v&agrave; hy vọng.</p>\r\n\r\n<p>Ng&ocirc;n ngữ đẹp, hiền h&ograve;a, gần gũi v&agrave; s&acirc;u lắng.</p>\r\n\r\n<p>Sản phẩm ch&iacute;nh h&atilde;ng từ NXB Trẻ, đảm bảo chất lượng v&agrave; uy t&iacute;n.</p>\r\n', '<p>H&agrave;nh l&yacute; hư v&ocirc;.<br />\r\nĐ&oacute; l&agrave; thứ duy nhất c&oacute; thể mang theo.<br />\r\nV&agrave;o đ&uacute;ng khi bạn nhận ra c&oacute; bao nhi&ecirc;u đồ đạc cũng chẳng lấp nổi biển trong l&ograve;ng.<br />\r\nV&agrave;o đ&uacute;ng khi bạn c&oacute; qu&aacute; nhiều thứ để nh&igrave;n nhận lại trước v&agrave; trong những cuộc chia tay.&nbsp;<br />\r\nV&agrave;o đ&uacute;ng khi bạn hiểu c&aacute;ch những mối quan hệ biến dạng sau mỗi cuộc chuyển dời, nhất l&agrave; giữa người với người.<br />\r\nV&agrave;o đ&uacute;ng khi bạn biết l&agrave; m&igrave;nh c&oacute; thể bu&ocirc;ng, nhẹ kh&ocirc;ng.<br />\r\nH&agrave;nh l&yacute; hư v&ocirc; l&agrave; tập tản văn mới nhất của nh&agrave; văn Nguyễn Ngọc Tư. Đọc n&oacute;, người ta kh&oacute; l&ograve;ng ngăn cản được nỗi buồn, m&agrave; cũng kh&ocirc;ng muốn ngăn cản nỗi buồn bởi cuối d&ograve;ng chảy cảm x&uacute;c ấy l&agrave; sự đồng cảm, hy vọng v&agrave; cả dỗ d&agrave;nh.&nbsp;<br />\r\nMột tập tản văn đẹp, hiền, mộc mạc v&agrave; s&acirc;u lắng chứa đựng tấm l&ograve;ng của người viết.</p>\r\n', 1, 43),
(96, 'Tiểu Song U Ký', '119', '125800', 53, '1715100090_30.jpg.webp', '<p>Nghỉ ngơi thư gi&atilde;n t&acirc;m hồn.</p>\r\n\r\n<p>Thưởng thức hương hoa lan trong cơn gi&oacute; nhẹ.</p>\r\n\r\n<p>G&oacute;p phần khơi gợi gi&aacute; trị tốt đẹp trong kho t&agrave;ng cổ học.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Tiểu song u k&yacute;&rdquo; gồm 12 quyển, l&agrave; tập t&ugrave;y b&uacute;t do Trần Kế Nho thời nh&agrave; Minh bi&ecirc;n soạn. Cả cuốn s&aacute;ch thoạt tr&ocirc;ng như một bản ghi ch&eacute;p những điều tai nghe mắt thấy v&agrave; suy nghĩ của t&aacute;c giả, nhưng thực chất l&agrave; những điều t&aacute;c giả cảm v&agrave; ngộ. Cảm về một nh&acirc;n sinh mu&ocirc;n h&igrave;nh vạn trạng, v&agrave; ngộ về một tấm ch&acirc;n t&igrave;nh của thế gian, một &ldquo;ch&acirc;n đế&rdquo; của cuộc sống. Trong cuộc sống hối hả ng&agrave;y nay, việc đọc cuốn s&aacute;ch Triết l&yacute; nh&acirc;n sinh trong xử thế Tam đại kỳ thư l&agrave; sự nghỉ ngơi thư gi&atilde;n, v&iacute; như uống một t&aacute;ch tr&agrave; thơm tr&ecirc;n b&agrave;n tr&agrave; t&acirc;m hồn, thưởng thức hương hoa lan thoảng đưa trong cơn gi&oacute; nhẹ.</p>\r\n\r\n<p>Cuốn s&aacute;ch n&agrave;y chọn lọc tr&iacute;ch dẫn những c&acirc;u hay nhất trong bốn quyển đầu vốn được coi l&agrave; gi&agrave;u tr&iacute; tuệ triết l&yacute; nh&acirc;n sinh, k&egrave;m theo phần chữ H&aacute;n để người đọc thấy được vẻ đẹp nguy&ecirc;n thủy của c&acirc;u văn, c&ugrave;ng phần dịch nghĩa v&agrave; lời b&igrave;nh, gi&uacute;p l&agrave;m s&aacute;ng tỏ tư tưởng của nguy&ecirc;n văn. Hy vọng cuốn s&aacute;ch sẽ g&oacute;p phần gạn đục khơi trong, ph&aacute;t huy c&aacute;c gi&aacute; trị tốt đẹp trong kho t&agrave;ng cổ học</p>\r\n', 1, 43),
(97, 'Đong Tấm Lòng', '120', '70800', 78, '1715100212_31.jpg.webp', '<p>Nguyễn Ngọc Tư viết về cuộc sống miền qu&ecirc; ch&acirc;n thực v&agrave; đầy cảm x&uacute;c.</p>\r\n\r\n<p>T&aacute;c phẩm đem đến những c&acirc;u chuyện đa dạng về người v&agrave; văn h&oacute;a miền T&acirc;y.</p>\r\n\r\n<p>T&aacute;c giả tận dụng kh&ocirc;ng gian miền qu&ecirc; để lẩy ra những c&acirc;u chuyện đặc sắc.</p>\r\n', '<p>Tập tản văn mới của Nguyễn Ngọc Tư gửi gắm vui buồn, &acirc;u lo kh&ocirc;ng chỉ về th&acirc;n phận người n&ocirc;ng d&acirc;n miền T&acirc;y, m&agrave; c&ograve;n về bản sắc văn h&oacute;a, lịch sử, cội nguồn một v&ugrave;ng đất</p>\r\n\r\n<p>Nguyễn Ngọc Tư lu&ocirc;n l&agrave; một c&acirc;y b&uacute;t chắc tay khi viết về con người, đời sống sinh hoạt miệt vườn. Chị tận dụng triệt để t&acirc;m hồn nhạy cảm vốn c&oacute; c&ugrave;ng cơ hội được đắm m&igrave;nh trong kh&ocirc;ng gian miền qu&ecirc; để lẩy ra những c&acirc;u chuyện kể. Cảnh sinh hoạt ấy trong trang viết Nguyễn Ngọc Tư hiện l&ecirc;n vừa y&ecirc;n tĩnh, thanh b&igrave;nh m&agrave; cũng vừa dậy s&oacute;ng, đầy ắp những đổi thay.</p>\r\n\r\n<p>Trong bức tranh đồng qu&ecirc; c&oacute; người gi&agrave;, trẻ nhỏ, c&oacute; những thanh ni&ecirc;n trai tr&aacute;ng, c&oacute; con x&oacute;m nhỏ với rặng hoa d&acirc;m bụt, những chiếc ghe vất vả ngược xu&ocirc;i m&ugrave;a gi&oacute; chướng, c&oacute; m&ugrave;a lụt nước về hay những c&acirc;u chuyện ma mị dọc đường gi&oacute; bụi giang hồ của miền T&acirc;y xa thẳm.</p>\r\n\r\n<p>Nhưng Nguyễn Ngọc Tư kh&ocirc;ng chỉ n&oacute;i về con người miền T&acirc;y sống gần với ruộng vườn, s&ocirc;ng nước, với thi&ecirc;n nhi&ecirc;n bằng t&acirc;m hồn cởi mở, ph&oacute;ng kho&aacute;ng, nghĩa kh&iacute;, h&agrave;o hiệp. Chị gần gũi với họ đủ để lẩy ra được cả những c&aacute;i nh&igrave;n phản biện về t&iacute;nh c&aacute;ch n&ocirc;ng d&acirc;n. Những đặc t&iacute;nh của th&oacute;i quen &ldquo;sống h&ocirc;m nay chẳng cần biết đến ng&agrave;y mai&rdquo;, t&iacute;nh &ldquo;chịu chơi, xả l&aacute;ng&rdquo; của người n&ocirc;ng d&acirc;n miệt vườn được chị ph&aacute;c lại với giọng văn tưởng như nhẹ nh&agrave;ng nhưng ẩn trong đấy l&agrave; một sự rưng rưng thương cảm. &ldquo;&hellip; Người đ&agrave;n b&agrave; k&eacute;o c&aacute; dưới ao l&ecirc;n đ&atilde;i kh&aacute;ch cho chồng, rồi bưng t&ocirc; cơm nguội ăn với muối ti&ecirc;u&rdquo; (b&agrave;i &ldquo;người nơi bi&ecirc;n giới&rdquo;). Hay c&acirc;u chuyện người ta b&agrave;y đặt đổi vợ đổi chồng cho nhau trong &ldquo;Miền T&acirc;y kh&ocirc;ng c&oacute; g&igrave; lạ&rdquo;, bởi theo chị ở miền T&acirc;y, kh&ocirc;ng c&oacute; chuyện g&igrave; l&agrave; kh&ocirc;ng thể xảy ra.</p>\r\n', 1, 43),
(98, 'Gáy Người Thì Lạnh', '121', '49500', 18, '1715100470_32.jpg.webp', '<p>Chất liệu giấy tốt , b&igrave;a cứng loại A0II.<br />\r\nM&agrave;u sắc trang giấy dịu nhẹ kết hợp c&acirc;u chữ sống động l&agrave;m thư gi&atilde;n t&acirc;m hồn người đọc.</p>\r\n', '<p>Qu&aacute; khứ l&agrave; kỷ niệm ấm &aacute;p, c&ograve;n tương lai l&agrave; những kh&aacute;t khao. Giữa hai miền thời gian đ&oacute;, những chuyến dong ruổi, d&ugrave; ngắn, qua ng&oacute;c ng&aacute;ch của l&agrave;ng qu&ecirc; hiện tại, đ&atilde; gi&uacute;p nh&agrave; văn viết n&ecirc;n những chi tiết hiện thực g&acirc;y nh&oacute;i buốt. &quot;G&aacute;y người th&igrave; lạnh&quot; giống như những lời trần t&igrave;nh (hay tự vấn) của t&aacute;c giả, đồng thời cũng l&agrave; chia sẻ đến những ai mong được kết nối với tự nhi&ecirc;n, th&egrave;m được thở &quot;những thứ kh&iacute; trời b&ecirc;n ngo&agrave;i c&aacute;nh cửa&quot;.</p>\r\n', 1, 43),
(99, 'Vật Chủ', '122', '155000', 34, '1715101137_33.jpg.webp', '', '<p>C&acirc;u chuyện hấp dẫn v&agrave; m&ecirc; hoặc về t&igrave;nh y&ecirc;u v&agrave; sự ki&ecirc;n định.</p>\r\n\r\n<p>Ng&ocirc;n ngữ sắc sảo v&agrave; cuốn h&uacute;t, tạo n&ecirc;n trải nghiệm đọc th&uacute; vị.</p>\r\n\r\n<p>K&iacute;ch thước s&aacute;ch lớn v&agrave; b&igrave;a mềm dễ cầm nắm v&agrave; đọc.</p>\r\n', 1, 41),
(100, 'Học Viện Viễn Thám', '123', '112500', 96, '1715101249_34.jpg.webp', '<p>T&aacute;c giả: Trudi Trueit</p>\r\n\r\n<p>Khu&ocirc;n Khổ: 15x22.5 cm</p>\r\n\r\n<p>Số trang: 224</p>\r\n\r\n<p>Định dạng: b&igrave;a mềm</p>\r\n\r\n<p>Trọng lượng: 410 gram</p>\r\n\r\n<p>Bộ s&aacute;ch: Học viện viễn th&aacute;m</p>\r\n\r\n<p>Xuất bản: NXB Kim Đồng</p>\r\n\r\n<p>Ng&agrave;y ph&aacute;t h&agrave;nh: 04/04/2020</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>TỪ RỪNG GI&Agrave; RẬM RẠP ĐẾN B&Igrave;NH NGUY&Ecirc;N BAO LA, TỪ QU&Ecirc; NH&Agrave; LO&Agrave;I KHỈ ĐỘT N&Uacute;I RỤT R&Egrave; TỚI CHỐN NGỤ CƯ CỦA NHỮNG ĐẠI MI&Ecirc;U DŨNG M&Atilde;NH.</p>\r\n\r\n<p>Miền hoang mạc m&ecirc;nh m&ocirc;ng t&agrave;n khốc r&igrave;nh rập nhiều hiểm nguy - giấc mơ của mọi nh&agrave; th&aacute;m hiểm - l&agrave; điểm dừng ch&acirc;n tiếp theo của đo&agrave;n học vi&ecirc;n Học viện Viễn Th&aacute;m. Vừa đặt ch&acirc;n l&ecirc;n v&ugrave;ng đất của c&aacute;t v&agrave; gi&oacute;, biến cố ập đến, v&agrave; lần n&agrave;y, một trong những người bạn th&acirc;n thiết nhất của Cruz trở th&agrave;nh nạn nh&acirc;n bất đắc dĩ.</p>\r\n\r\n<p>&ldquo;T&igrave;nh tiết xuất sắc c&ugrave;ng bộ ảnh chụp v&agrave; tranh vẽ m&agrave;u khiến cuốn s&aacute;ch hấp dẫn cả những nh&agrave; viễn th&aacute;m đầy kh&aacute;t khao lẫn những độc giả t&igrave;nh cờ. C&aacute;i kết mở chắc chắn thu h&uacute;t độc giả đ&oacute;n đọc c&aacute;c tập sau.&rdquo; - School Library Journal</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 41),
(101, 'Tam Thể 1', '124', '105000', 20, '1715101388_35.jpg.webp', '<p>C&acirc;u chuyện h&ugrave;ng tr&aacute;ng, kịch t&iacute;nh v&agrave; triết l&yacute;.</p>\r\n\r\n<p>Tri thức khoa học th&uacute; vị qua tr&ograve; chơi Tam Thể.</p>\r\n\r\n<p>Dịch giả Lục Hương mang lại phi&ecirc;n bản dịch chất lượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>U&ocirc;ng Diểu, vị gi&aacute;o sư về vật liệu nano ng&agrave;y n&agrave;o cũng đăng nhập v&agrave;o &ldquo;Tam Thể&rdquo;. Tại tr&ograve; chơi online đ&oacute;, anh đắm ch&igrave;m trong một thế giới kh&aacute;c, nơi một nền văn minh c&oacute; thể chỉ k&eacute;o d&agrave;i v&agrave;i ng&agrave;y, bầu trời c&oacute; thể xuất hiện ba mặt trời c&ugrave;ng l&uacute;c v&agrave; con người c&ograve;n phải biến th&agrave;nh x&aacute;c kh&ocirc; để sinh tồn.</p>\r\n\r\n<p>Nhưng anh kh&ocirc;ng thể ngờ, thế giới khắc nghiệt trong Tam Thể l&agrave; c&oacute; thực, chỉ c&aacute;ch tr&aacute;i đất chừng bốn năm &aacute;nh s&aacute;ng, v&agrave; tr&ograve; chơi ảo kia lại l&agrave; một c&aacute;nh cửa để những sinh vật của thế giới ấy bước đến x&acirc;m chiếm địa cầu n&agrave;y. Kinh ho&agrave;ng, U&ocirc;ng Diểu t&igrave;m mọi c&aacute;ch ngăn chặn điều đ&oacute;. Nhưng anh, cũng như cả địa cầu, kh&ocirc;ng biết rằng, c&aacute;nh cửa nọ đ&atilde; được mở toang, từ mấy chục năm về trước.</p>\r\n\r\n<p>H&ugrave;ng tr&aacute;ng, kịch t&iacute;nh, triết l&yacute;, n&ecirc;n thơ, với những tri thức khoa học th&uacute; vị, Tam thể l&agrave; phần mở đầu mang cảm hứng sử thi cho tam bộ kh&uacute;c của Lưu Từ H&acirc;n. Sau tất cả những mưu toan ly kỳ, nham hiểm, những nỗ lực tưởng chừng tuyệt vọng để sinh tồn, c&acirc;u hỏi c&ograve;n đọng lại, kh&ocirc;ng phải &#39;Lo&agrave;i người n&ecirc;n l&agrave;m g&igrave; để đối ph&oacute; với sự x&acirc;m lăng của Tam Thể?&#39;, m&agrave; l&agrave; &#39;Lo&agrave;i người đ&atilde; l&agrave;m g&igrave; ch&iacute;nh m&igrave;nh?&#39;</p>\r\n', 1, 41),
(102, 'Tam Thể 2', '125', '157000', 23, '1715101495_36.jpg.webp', '<p>Cốt truyện hấp dẫn, kịch t&iacute;nh v&agrave; đầy bất ngờ.</p>\r\n\r\n<p>M&ocirc; tả tương lai s&aacute;ng tạo, chi tiết v&agrave; đầy b&iacute; ẩn.</p>\r\n\r\n<p>Ng&ocirc;n ngữ sắc sảo, hấp dẫn v&agrave; thu h&uacute;t sự ch&uacute; &yacute;.</p>\r\n', '<p>Hạm đội Tam Thể đ&atilde; l&ecirc;n đường, bắt đầu h&agrave;nh tr&igrave;nh đằng đẵng bốn năm &aacute;nh s&aacute;ng đến miền đất mới. V&agrave; con người Tr&aacute;i đất, từ nguy&ecirc;n thủ đến người d&acirc;n, cũng bước v&agrave;o chuẩn bị đ&oacute;n tiếp kẻ địch từ vũ trụ bốn trăm năm sau.<br />\r\n<br />\r\nTr&ecirc;n Tr&aacute;i đất nơi khoa học đ&atilde; bị kh&oacute;a chết, dưới sự gi&aacute;m s&aacute;t thời gian thực của thế giới Tam Thể, đ&atilde; nổi l&ecirc;n một kế hoạch v&ocirc; tiền kho&aacute;ng hậu: Kế hoạch Diện B&iacute;ch. Bốn chiến lược gia đại t&agrave;i, được trao quyền đ&aacute;nh lừa to&agrave;n Tr&aacute;i đất v&igrave; trọng tr&aacute;ch qua mặt người Tam Thể. Nhưng chỉ một trong số đ&oacute; biết về &ldquo;Khu rừng đen tối&rdquo;, b&iacute; mật khủng khiếp về bản chất của vũ trụ.<br />\r\n<br />\r\nPhần thứ hai tam bộ kh&uacute;c của Lưu Từ H&acirc;n, Khu rừng đen tối l&agrave; một pho chiến quốc sử kỳ vĩ v&agrave; kịch t&iacute;nh. Đ&oacute; c&ograve;n l&agrave; một bức tranh gai người về tương lai, khi để sống s&oacute;t giữa khu rừng đen tối ấy, kh&ocirc;ng chỉ Tr&aacute;i đất đ&atilde; thay đổi ho&agrave;n to&agrave;n diện mạo, m&agrave; con người cũng kh&ocirc;ng c&ograve;n giống như con người.</p>\r\n', 1, 41),
(103, 'Tam Thể 3', '126', '203000', 33, '1715101610_37.jpg.webp', '<p>Sử dụng kiến thức khoa học vững chắc để x&acirc;y dựng c&acirc;u chuyện hấp dẫn.</p>\r\n\r\n<p>Kết hợp giữa cuộc sống chốn nh&acirc;n gian v&agrave; cuộc sống trong tưởng tượng.</p>\r\n\r\n<p>Tạo ra một c&acirc;u chuyện cổ t&iacute;ch v&agrave; h&agrave;nh tr&igrave;nh của mỗi nền văn minh.</p>\r\n', '<p>Đe dọa khu rừng đen tối gi&uacute;p nh&acirc;n loại duy tr&igrave; thế c&acirc;n bằng chiến lược với thế giới Tam Thể suốt s&aacute;u mươi năm. Song rốt cuộc, một đ&ograve;n tấn c&ocirc;ng bất ngờ đ&atilde; l&agrave;m thay đổi cục diện. Lo&agrave;i người cận kề bờ vực diệt vong, buộc phải ph&aacute;t đi th&ocirc;ng tin tọa độ của hệ sao Tam Thể. Nhưng rồi, họ đối mặt với một vấn đề sinh tồn mới: vị tr&iacute; của Hệ Mặt trời sớm muộn g&igrave; cũng bị lộ trước những tay &ldquo;thợ săn&rdquo; vũ trụ. Mọi hy vọng đều gửi gắm v&agrave;o việc giải m&atilde; ba c&acirc;u chuyện cổ t&iacute;ch được truyền về từ một c&aacute; thể nh&acirc;n loại giấu m&igrave;nh trong nội bộ hạm đội người ngo&agrave;i h&agrave;nh tinh.<br />\r\n<br />\r\nTử Thần sống m&atilde;i l&agrave; kết th&uacute;c ho&agrave;nh tr&aacute;ng v&agrave; ho&agrave;n hảo cho tam bộ kh&uacute;c của Lưu Từ H&acirc;n, như một thi&ecirc;n sử thi về h&agrave;nh tr&igrave;nh của mỗi nền văn minh, từ khi thức tỉnh, &ldquo;chập chững bước ra ngo&agrave;i, bay l&ecirc;n, bay c&agrave;ng l&uacute;c c&agrave;ng nhanh, bay c&agrave;ng l&uacute;c c&agrave;ng xa, cuối c&ugrave;ng th&igrave; h&ograve;a l&agrave;m một với vận mệnh của vũ trụ.&rdquo;&nbsp;<br />\r\n&nbsp;</p>\r\n', 1, 41),
(104, 'Klara Và Mặt Trời ', '127', '162000', 47, '1715101742_38.jpg.webp', '<p>C&acirc;u chuyện độc đ&aacute;o v&agrave; gợi mở về thế giới của Klara, một người bạn c&oacute; tr&iacute; tuệ nh&acirc;n tạo.</p>\r\n\r\n<p>Giọng kể trung t&iacute;nh, nhạy cảm v&agrave; chuẩn x&aacute;c của Klara.</p>\r\n\r\n<p>T&aacute;c giả Kazuo Ishiguro l&agrave; nh&agrave; văn nổi tiếng v&agrave; đ&atilde; nhận nhiều giải thưởng.</p>\r\n', '<p>Ngồi sau quầy k&iacute;nh trưng b&agrave;y, Klara nh&igrave;n ra thế giới. Một thế giới, thoạt tr&ocirc;ng, rất giống thế giới của ch&uacute;ng ta: vẫn những nh&acirc;n vi&ecirc;n văn ph&ograve;ng, người chạy bộ, kh&aacute;ch du lịch, taxi hối hả lại qua; vẫn Mặt Trời đỏ ối ng&agrave;y ng&agrave;y di chuyển từ b&ecirc;n n&agrave;y sang b&ecirc;n kia những t&ograve;a nh&agrave; chọc trời. Nhưng c&oacute; g&igrave; đ&oacute; rất kh&aacute;c, bởi đ&ocirc;i mắt đang quan s&aacute;t tỉ mỉ tất cả những điều đ&oacute; kh&ocirc;ng thuộc về con người. Klara kh&ocirc;ng phải l&agrave; người. C&ocirc; l&agrave; một AF, một người bạn c&oacute; tr&iacute; tuệ nh&acirc;n tạo. Klara l&agrave; một robot được tạo ra để bầu bạn với trẻ em.</p>\r\n\r\n<p>Qua giọng kể đều đều trung t&iacute;nh, dưới g&oacute;c nh&igrave;n c&oacute; phần ng&acirc;y thơ nhưng nhạy cảm v&agrave; ho&agrave;n to&agrave;n chuẩn x&aacute;c của Klara, những bất ổn cứ thế tho&aacute;ng hiện ra đ&acirc;u đ&acirc;y, mơ hồ nhưng cũng đủ khiến ta bứt rứt. Để rồi cuối c&ugrave;ng, một phần sự thật xấu x&iacute; về c&aacute;i thế giới ấy bị lột trần một c&aacute;ch mạnh mẽ, đau l&ograve;ng.</p>\r\n\r\n<p>L&agrave;m người c&oacute; nghĩa l&agrave; g&igrave;? Kẻ nắm r&otilde; đ&aacute;p &aacute;n cho c&acirc;u hỏi ấy, rốt cuộc, lại kh&ocirc;ng phải một con người bằng xương bằng thịt, d&ugrave; c&oacute; lẽ, c&ograve;n &ldquo;con người hơn cả con người&rdquo;.</p>\r\n', 1, 41),
(105, 'Diệt Chủng', '128', '209000', 31, '1715102739_39.jpg.webp', '<p>C&acirc;u chuyện hấp dẫn, bất ngờ, lu&ocirc;n tạo cảm gi&aacute;c hồi hộp v&agrave; t&ograve; m&ograve;.</p>\r\n\r\n<p>Thế giới đầy m&agrave;u sắc, chi tiết, gi&uacute;p người đọc h&ograve;a m&igrave;nh v&agrave;o c&acirc;u chuyện.</p>\r\n\r\n<p>Kết hợp h&agrave;nh động v&agrave; t&acirc;m l&yacute;, tạo n&ecirc;n cốt truyện phong ph&uacute; v&agrave; đa chiều.</p>\r\n', '<p>Koga Kento, cậu sinh vi&ecirc;n cao học chuy&ecirc;n ng&agrave;nh h&oacute;a dược, đột ngột nhận được email từ người cha qu&aacute; cố. Bức thư tuyệt mệnh lạ l&ugrave;ng đ&atilde; dẫn cậu lần ra một ph&ograve;ng th&iacute; nghiệm tư nh&acirc;n b&iacute; ẩn. Cha cậu &ndash; một&nbsp;<br />\r\nnh&agrave; virus học &ndash; đang nghi&ecirc;n cứu thứ g&igrave; ở đ&oacute;?&nbsp;</p>\r\n\r\n<p>V&igrave; con trai mắc bệnh nan y, tay l&iacute;nh đ&aacute;nh thu&ecirc; Jonathan Yeager nhận lời g&aacute;nh v&aacute;c một nhiệm vụ tuyệt mật. Với th&ocirc;ng tin duy nhất &ldquo;đ&oacute; l&agrave; một c&ocirc;ng việc v&igrave; nh&acirc;n loại&rdquo;, Yeager gia nhập một đội &aacute;m s&aacute;t, th&acirc;m nhập v&agrave;o rừng rậm của đất nước Congo đang bị chiến tranh t&agrave;n ph&aacute;.&nbsp;<br />\r\nKhi số phận của hai kẻ xa lạ ấy giao nhau, một sự thật chấn động được h&eacute; mở</p>\r\n\r\n<p>Takano Kazuaki sinh năm 1964 tại Tokyo, l&agrave; một tiểu thuyết gia v&agrave; người viết kịch bản phim. &Ocirc;ng l&agrave; th&agrave;nh vi&ecirc;n Hiệp hội nh&agrave; văn viết truyện kỳ b&iacute; Nhật Bản.<br />\r\nT&aacute;c phẩm Diệt chủng đ&atilde; đạt giải Yamada Futaro lần thứ 2, giải thưởng của Hiệp hội nh&agrave; văn viết truyện kỳ b&iacute; Nhật Bản lần thứ 65, lọt v&agrave;o danh s&aacute;ch đề cử giải thưởng Văn học Yoshikawa Eiji lần thứ 33 v&agrave; giải Naoki lần thứ 145.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 43),
(106, '451 Độ F', '129', '76100', 68, '1715103020_40.jpg.webp', '<p>M&ocirc; tả ch&acirc;n thực v&agrave; sắc b&eacute;n về x&atilde; hội hiện đại v&agrave; c&ocirc;ng nghệ.</p>\r\n\r\n<p>T&aacute;c giả c&oacute; c&aacute;i nh&igrave;n ti&ecirc;n tri về tương lai x&atilde; hội.</p>\r\n\r\n<p>K&iacute;ch thước s&aacute;ch nhỏ gọn, dễ mang theo.</p>\r\n', '<p>H&atilde;y mường tượng một thế giới nơi truyền h&igrave;nh thống trị v&agrave; văn chương ngấp ngh&eacute; tr&ecirc;n bờ tuyệt chủng, nơi th&ocirc;ng tin n&ocirc;ng cạn được tung h&ocirc; c&ograve;n tri thức v&agrave; &yacute; tưởng th&igrave; bị ruồng rẫy, nơi t&agrave;ng trữ s&aacute;ch l&agrave; phạm ph&aacute;p, ta c&oacute; thể bị bắt chỉ v&igrave; tản bộ tr&ecirc;n vỉa h&egrave;, c&ograve;n nhiệm vụ của những người l&iacute;nh kh&ocirc;ng phải cứu hỏa m&agrave; l&agrave; ch&acirc;m mồi cho những đ&aacute;m ch&aacute;y&hellip;</p>\r\n\r\n<p>Khi khắc họa c&aacute;i x&atilde; hội giả tưởng ấy qua con mắt nh&igrave;n khủng hoảng niềm tin của anh l&iacute;nh ph&oacute;ng hỏa Montag, Ray Bradbury chắc kh&ocirc;ng thể ngờ v&agrave;o t&iacute;nh ti&ecirc;n tri khủng khiếp của cuốn s&aacute;ch. X&atilde; hội ch&uacute;ng ta đang sống ng&agrave;y nay giống với những g&igrave; Bradbury m&ocirc; tả đến r&ugrave;ng m&igrave;nh: một nền văn minh lệ thuộc qu&aacute; nhiều v&agrave;o truyền th&ocirc;ng, giải tr&iacute; v&agrave; c&ocirc;ng nghệ. Bởi lẽ đ&oacute;, hơn s&aacute;u chục năm kể từ lần đầu xuất bản, 451 độ F vẫn đủ sức khiến bất kỳ ai đọc n&oacute; phải sửng sốt v&agrave; cho&aacute;ng v&aacute;ng.</p>\r\n', 1, 43),
(107, 'Kinh Dịch', '130', '193150', 37, '1715103293_41.jpg.webp', '<p>Kinh Dịch l&agrave; t&aacute;c phẩm kinh điển l&acirc;u đời nhất của văn h&oacute;a Trung Hoa cổ đại.</p>\r\n\r\n<p>S&aacute;ch c&oacute; 384 lời khuy&ecirc;n hữu &iacute;ch, gi&uacute;p bạn hiểu r&otilde; về quy luật v&agrave; phương ph&aacute;p nhận thức.</p>\r\n\r\n<p>S&aacute;ch viết gần gũi, dễ hiểu, ph&ugrave; hợp cho người mới bắt đầu t&igrave;m hiểu về Kinh Dịch.</p>\r\n', '<p>Dịch l&agrave; biến đổi, tức l&agrave; t&ugrave;y thời biến đổi để theo Đạo. N&oacute; l&agrave; thứ s&aacute;ch rộng lớn đầy đủ, hầu thuận theo lẽ số mệnh&nbsp;<em>(t&iacute;nh mệnh n&agrave;y chỉ về t&iacute;nh chất số mệnh, kh&ocirc;ng phải chữ t&iacute;nh mệnh chỉ về sinh mạng)</em>, th&ocirc;ng đạt cớ u minh, hiểu hết t&igrave;nh trạng mu&ocirc;n vật m&agrave; bảo những c&aacute;ch mở mang c&aacute;c vật, l&agrave;m th&agrave;nh c&aacute;c việc.</p>\r\n\r\n<p>Khổng Tử đ&atilde; từng n&oacute;i: &ldquo;Nếu cho t&ocirc;i sống th&ecirc;m v&agrave;i năm nữa, th&igrave; cho d&ugrave; 50 tuổi học Kinh Dịch cũng kh&ocirc;ng phải l&agrave; sai lầm&rdquo;.</p>\r\n\r\n<p>C&oacute; thể n&oacute;i,&nbsp;Kinh Dịch&nbsp;l&agrave; một trước t&aacute;c kinh điển l&acirc;u đời nhất, kết tinh tr&iacute; tuệ của văn h&oacute;a Trung Hoa cổ đại.&nbsp;Kinh Dịch&nbsp;ph&aacute;t hiện t&iacute;nh quy luật v&agrave; phương ph&aacute;p nhận thức, dự đo&aacute;n, xử l&yacute; sự vật, v&agrave; với &yacute; ngh&iacute; nghĩa phương ph&aacute;p luận n&agrave;y, n&oacute; c&oacute; ảnh hưởng quan trọng đối với nhiều lĩnh vực như triết học, khoa học x&atilde; hội, văn h&oacute;a nghệ thuật&hellip; của Trung Quốc từ xưa đến nay.</p>\r\n\r\n<p>Trong&nbsp;Kinh Dịch&nbsp;c&oacute; 384 h&agrave;o, c&oacute; nghĩa l&agrave; c&oacute; 384 lời khuy&ecirc;n hữu &iacute;ch.</p>\r\n\r\n<p>Lật mở từng trang s&aacute;ch, bạn sẽ c&oacute; cảm gi&aacute;c nhẹ nh&agrave;ng, khoan kho&aacute;i, bạn hẳn sẽ kh&ocirc;ng nghĩ rằng quản l&yacute; trong học thuật truyền thống lại được viết ra gần gũi, dễ hiểu đến thế. Ở đ&oacute;, bạn kh&ocirc;ng hề thấy bất kỳ hơi thở n&agrave;o mang &acirc;m hưởng nghi&ecirc;n cứu Nho gi&aacute;o, c&agrave;ng kh&ocirc;ng hề thấy chỗ n&agrave;o kh&oacute; hiểu cả. Đ&aacute;ng qu&yacute; hơn l&agrave;, bạn sẽ thấy mỗi một điểm trong cuốn s&aacute;ch n&agrave;y đều li&ecirc;n quan mật thiết đến c&ocirc;ng việc của bản th&acirc;n m&igrave;nh. Mỗi một quan điểm, mỗi một kiến giải trong đ&oacute; đều gi&uacute;p ch&uacute;ng ta tho&aacute;t ra khỏi kh&oacute; khăn v&agrave; cản trở để trưởng th&agrave;nh trong c&ocirc;ng việc.</p>\r\n', 1, 42),
(108, 'Bí Mật Âm Dương', '131', '290500', 37, '1715104076_42.jpg.webp', '<p>Cung cấp kiến thức cơ bản v&agrave; ho&agrave;n chỉnh về Kinh dịch v&agrave; phong thủy.</p>\r\n\r\n<p>T&aacute;c giả chia sẻ kinh nghiệm c&aacute; nh&acirc;n để n&acirc;ng cao dự đo&aacute;n ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>Giải th&iacute;ch quy luật &acirc;m dương ngũ h&agrave;nh v&agrave; vận dụng tổng hợp mối quan hệ sinh khắc.</p>\r\n', '<p>Trong c&aacute;c cuốn s&aacute;ch Kinh dịch giản lược, Kinh dịch dự đo&aacute;n, Kinh dịch với nh&acirc;n dạng v&agrave; phong thủy t&ocirc;i đ&atilde; từng bước trao đổi với bạn đọc c&aacute;c kiến thức cơ bản v&agrave; ho&agrave;n chỉnh ở cuốn Nh&acirc;n dạng với dự đo&aacute;n.</p>\r\n\r\n<p>Trong cuốn s&aacute;ch n&agrave;y t&ocirc;i muốn ph&acirc;n t&iacute;ch để bạn đọc hiểu một số kiến thức c&oacute; bản m&agrave; c&aacute;c cuốn s&aacute;ch trước ch&uacute;ng ta chỉ biết c&ocirc;ng nhận v&agrave; l&agrave;m theo, đồng thời đ&atilde; đi s&acirc;u th&ecirc;m về 2 vấn đề c&oacute; t&iacute;nh thời sự trong cuộc s&ocirc;ng v&agrave; kiến tr&uacute;c phong thủy.</p>\r\n\r\n<p>Để bạn đọc c&oacute; thể tự n&acirc;ng cao kiến thức , dự đo&aacute;n dịch ch&iacute;nh x&aacute;c, đa dạng, t&ocirc;i n&ecirc;u một số kinh nghiệm của m&igrave;nh.</p>\r\n\r\n<p>+ Nắm chắc c&aacute;c quy luật của &acirc;m dương ngũ h&agrave;nh</p>\r\n\r\n<p>+ Ph&aacute;t huy tốt kết quả nghi&ecirc;n cứu của tiền nh&acirc;n</p>\r\n\r\n<p>+ Vận dụng tổng hợp mối quan hệ sinh khắc, đa dạng h&oacute;a của dự đo&aacute;n</p>\r\n', 1, 42),
(109, 'Phong Thuỷ Hoá Giải', '132', '136000', 23, '1715104335_43.jpg.webp', '<p>Loại b&igrave;a : b&igrave;a mềm tay gấp<br />\r\nSố trang : 400<br />\r\nNh&agrave; xuất bản : nh&agrave; xuất bản Thanh Ho&aacute;</p>\r\n', '<p>Cung cấp kiến thức cơ bản v&agrave; ho&agrave;n chỉnh về Kinh dịch v&agrave; phong thủy.</p>\r\n\r\n<p>T&aacute;c giả chia sẻ kinh nghiệm c&aacute; nh&acirc;n để n&acirc;ng cao dự đo&aacute;n ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p>Giải th&iacute;ch quy luật &acirc;m dương ngũ h&agrave;nh v&agrave; vận dụng tổng hợp mối quan hệ sinh khắc.</p>\r\n', 1, 42),
(110, 'Phong Thuỷ Chung Cư', '133', '399000', 12, '1715104457_44.jpg.webp', '<p>Viết bởi chuy&ecirc;n gia phong thủy nổi tiếng Joey Yap.</p>\r\n\r\n<p>Cung cấp c&acirc;u trả lời chi tiết về phong thủy trong căn hộ hoặc chung cư.</p>\r\n\r\n<p>K&iacute;ch thước s&aacute;ch nhỏ gọn, dễ d&agrave;ng mang theo v&agrave; đọc.</p>\r\n', '<p>T&igrave;m một căn hộ hoặc chung cư tốt kh&ocirc;ng bao giờ l&agrave; một nhiệm vụ dễ d&agrave;ng nhưng phải đảm bảo rằng chung cư đ&oacute; c&oacute; hợp phong thủy ? V&agrave; l&agrave;m thế n&agrave;o ch&iacute;nh x&aacute;c để bạn &aacute;p dụng Phong Thủy cho một căn hộ hoặc chung cư hoặc nh&agrave; ở cao tầng? Những c&acirc;u hỏi n&agrave;y v&agrave; nhiều hơn nữa được trả lời bởi chuy&ecirc;n gia tư vấn phong thủy nổi tiếng v&agrave; huấn luyện vi&ecirc;n bậc thầy Joey Yap trong&nbsp;Phong Thủy Cho Người Mua Nh&agrave; Chung Cư.</p>\r\n\r\n<p>Mục lục:</p>\r\n\r\n<p>Chương 1: Căn hộ v&agrave; nh&agrave;: Quả t&aacute;o v&agrave; tr&aacute;i cam trong phong thuỷ ?</p>\r\n\r\n<p>Chương 2: C&aacute;c phương ph&aacute;p v&agrave; c&ocirc;ng cụ đ&aacute;nh gi&aacute;&nbsp;</p>\r\n\r\n<p>Chương 3: Đ&aacute;nh gi&aacute; h&igrave;nh thế ngoại cảnh&nbsp;</p>\r\n\r\n<p>Chương 4: Chọn to&agrave; nh&agrave;</p>\r\n\r\n<p>Chương 5: Căn hộ v&agrave; kiến tr&uacute;c phi truyền thống</p>\r\n\r\n<p>Chương 6: Lối v&agrave;o ch&iacute;nh</p>\r\n\r\n<p>Chương 7: Chọn căn hộ</p>\r\n\r\n<p>Chương 8: D&ugrave;ng phi tinh để chọn căn hộ</p>\r\n\r\n<p>Chương 9: Hướng dẫn thực h&agrave;nh</p>\r\n', 1, 42),
(111, 'Mai Hoa Dịch Số', '134', '67800', 45, '1715104578_45.jpg.webp', '<p>Ph&eacute;p gieo quẻ linh hoạt, &aacute;p dụng được ở mọi nơi, mọi l&uacute;c.</p>\r\n\r\n<p>Ứng nghiệm đặc sắc, được ứng dụng rộng r&atilde;i trong mọi lĩnh vực.</p>\r\n\r\n<p>Dựa tr&ecirc;n Kinh Dịch - cuốn s&aacute;ch kinh điển đứng đầu lục kinh.</p>\r\n', '<p><strong>Mai hoa dịch số</strong>&nbsp;l&agrave; bộ kỳ thư độc đ&aacute;o trong lịch sử văn h&oacute;a Trung Hoa, l&agrave; vi&ecirc;n ngọc qu&yacute; của khoa chi&ecirc;m bốc dự đo&aacute;n học, tương truyền do Thiệu Khang Tiết, đại triết gia đời Tống bi&ecirc;n soạn.</p>\r\n\r\n<p>Nền tảng của Mai hoa dịch số l&agrave; Kinh Dịch, nội dung của Mai hoa dịch số l&agrave; c&aacute;c ph&eacute;p gieo quẻ v&agrave; đo&aacute;n quẻ rất linh hoạt, c&oacute; thể gieo quẻ v&agrave; đo&aacute;n quẻ ở bất cứ nơi n&agrave;o, bất cứ l&uacute;c n&agrave;o, mức độ ứng nghiệm hết sức đặc sắc, xưa nay l&agrave; m&ocirc;n chi&ecirc;m bốc được ứng dụng rộng r&atilde;i v&agrave; phổ biến trong mọi lĩnh vực của đời sống. Mai hoa dịch số cũng như c&aacute;c ph&eacute;p chi&ecirc;m bốc kh&aacute;c đều dựa tr&ecirc;n tượng số b&aacute;t qu&aacute;i, những tượng số n&agrave;y thể hiện ở hầu hết mọi sự vật, tất cả đều xuất ph&aacute;t từ Kinh Dịch, cuốn s&aacute;ch kinh điển đứng đầu lục kinh.</p>\r\n', 1, 42),
(112, 'Bí Ẩn Bàn Tay', '135', '57500', 41, '1715104670_46.jpg.webp', '<p>Cung cấp th&ocirc;ng tin về mối li&ecirc;n hệ giữa b&agrave;n tay v&agrave; tố chất con người.</p>\r\n\r\n<p>Được viết bởi nh&oacute;m t&aacute;c giả c&oacute; kiến thức s&acirc;u về lĩnh vực n&agrave;y.</p>\r\n\r\n<p>Kiến thức hệ thống v&agrave; đầy đủ về quan hệ giữa b&agrave;n tay v&agrave; tố chất con người.</p>\r\n', '<p>Những nghi&ecirc;n cứu khoa học mới nhất cho thấy: Giữa n&atilde;o bộ v&agrave; b&agrave;n tay con ngời c&oacute; mối li&ecirc;n hệ kỳ diệu: Hoạt động của hai b&agrave;n tay l&agrave;m cho n&atilde;o bộ ph&aacute;t triển v&agrave; mọi th&ocirc;ng tin từ n&atilde;o bộ lu&ocirc;n thể hiện tr&ecirc;n b&agrave;n tay của con người. C&oacute; thể n&oacute;i b&agrave;n tay l&agrave; bộ n&atilde;o thứ hai của con người, những th&ocirc;ng tin về con người được m&atilde; h&oacute;a bởi ch&iacute;nh những đường n&eacute;t, đường v&acirc;n tr&ecirc;n b&agrave;n tay của họ.<br />\r\nTừ cổ xưa người Trung Quốc đ&atilde; quan t&acirc;m nghi&ecirc;n cứu t&igrave;m hiểu vấn đề n&agrave;y v&agrave; h&igrave;nh th&agrave;nh bộ m&ocirc;n thuật xem tướng tay. C&oacute; thể coi đ&acirc;y l&agrave; một trong những m&ocirc;n khoa học nghi&ecirc;n cứu về con người cổ xưa nhất. Trải qua qu&aacute; tr&igrave;nh ph&aacute;t triển v&agrave; biến đổi của lịch sử, n&oacute; lu&ocirc;n được nghi&ecirc;n cứu bổ khuyết v&agrave; c&oacute; những gi&aacute; trị thực tiễn kh&ocirc;ng thể phủ nhận, đặc biệt l&agrave; trong lĩnh vực y học.</p>\r\n\r\n<p>&ldquo;B&iacute; ẩn b&agrave;n tay&rdquo; l&agrave; cuốn s&aacute;ch giới thiệu một c&aacute;ch hệ thống, đầy đủ v&agrave; s&aacute;ng r&otilde; những quan niệm của người Trung Quốc cổ đại về quan hệ giữa những đường n&eacute;t, hoa văn v&agrave; c&aacute;c khu vực của b&agrave;n tay với tố chất của con người trong hiện tại tương lai theo nhiều g&oacute;c độ: Sức khỏe, tr&iacute; tuệ, kh&iacute; chất, đời sống, tư duy, năng lực.</p>\r\n\r\n<p>Hy vọng cuốn s&aacute;ch l&agrave; nguồn tham khảo gi&uacute;p bạn đọc c&oacute; th&ecirc;m tư liệu để t&igrave;m hiểu về nền tri thức văn h&oacute;a cổ Trung Hoa mu&ocirc;n m&agrave;u v&agrave; hấp dẫn c&ugrave;ng những b&iacute; ẩn m&agrave; khoa học trong tương lai cần l&yacute; giải.</p>\r\n', 1, 42),
(113, 'Dữ Liệu Bạch Kim', '136', '114900', 24, '1715105749_47.jpg.webp', '', '<p>Thi&ecirc;n t&agrave;i to&aacute;n học Tateshina Saki đ&atilde; tạo ra một chương tr&igrave;nh tổng hợp dữ liệu DNA của người d&acirc;n để hỗ trợ cảnh s&aacute;t trong việc kiểm so&aacute;t, ngăn chặn v&agrave; truy bắt tội phạm. Nhờ hệ thống dữ liệu n&agrave;y, tỷ lệ ph&aacute; &aacute;n của cảnh s&aacute;t tăng vọt. Tuy nhi&ecirc;n, hung thủ của vụ &aacute;n giết người li&ecirc;n ho&agrave;n NOT FOUND 13 vẫn nhởn nhơ ngo&agrave;i v&ograve;ng ph&aacute;p luật. Rốt cuộc, đối với hệ thống điều tra DNA, NOT FOUND 13 chỉ l&agrave; vấn đề thiếu dữ liệu, hay ch&iacute;nh l&agrave; một lỗ hổng b&iacute; ẩn cần được giải đ&aacute;p? Đ&acirc;u l&agrave; ch&igrave;a kh&oacute;a để mở ra kho &ldquo;Dữ liệu bạch kim&rdquo; b&iacute; ẩn?</p>\r\n', 1, 39),
(114, 'Bố Già', '137', '201500', 23, '1715106244_48.jpg.webp', '', '<p>Thế giới ngầm được phản &aacute;nh trong tiểu thuyết&nbsp;<em>Bố gi&agrave;</em>&nbsp;l&agrave; sự gặp gỡ giữa một b&ecirc;n l&agrave; &yacute; ch&iacute; cương cường v&agrave; nền tảng gia tộc chặt chẽ theo truyền thống mafia xứ Sicily với một b&ecirc;n l&agrave; x&atilde; hội Mỹ nhập nhằng đen trắng, mảnh đất m&agrave;u mỡ cho những cơ hội l&agrave;m ăn bất ch&iacute;nh hứa hẹn những m&oacute;n lợi kếch x&ugrave;. Trong thế giới ấy, h&igrave;nh tượng Bố gi&agrave; được t&aacute;c giả d&agrave;y c&ocirc;ng khắc họa đ&atilde; trở th&agrave;nh bức ch&acirc;n dung bất hủ trong l&ograve;ng người đọc. Từ một kẻ nhập cư tay trắng đến &ocirc;ng tr&ugrave;m tột đỉnh quyền uy, Don Vito Corleone l&agrave; con rắn hổ mang th&acirc;m trầm, nguy hiểm khiến kẻ th&ugrave; phải kiềng nể, e d&egrave;, nhưng cũng được bạn b&egrave;, th&acirc;n quyến xem như một đấng to&agrave;n năng đầy nghĩa kh&iacute;. Nh&acirc;n vật trung t&acirc;m ấy đồng thời cũng l&agrave; hiện th&acirc;n của một pho triết l&iacute; rất &ldquo;đời&rdquo; được nh&agrave;o nặn từ vốn sống của h&agrave;ng chục năm lăn lộn giữa chốn giang hồ bao phen v&agrave;o sinh ra tử, v&igrave; thế m&agrave; c&oacute; &yacute; kiến cho rằng&nbsp;<em>&ldquo;Bố gi&agrave;</em>&nbsp;l&agrave; sự tổng h&ograve;a của mọi hiểu biết.&nbsp;<em>Bố gi&agrave;</em>&nbsp;l&agrave; đ&aacute;p &aacute;n cho mọi c&acirc;u hỏi&rdquo;.</p>\r\n\r\n<p><br />\r\nVới cấu tứ ho&agrave;n hảo, cốt truyện kh&ocirc;ng thiếu những pha h&agrave;nh động gay cấn, t&igrave;nh tiết bất ngờ v&agrave; kh&ocirc;ng kh&iacute; k&igrave;nh địch đến nghẹt thở</p>\r\n', 1, 39),
(115, 'Phố Tan Màn Đêm', '138', '132000', 23, '1715107038_49.jpg.webp', '<p>Cốt truyện hấp dẫn, g&acirc;y cấn v&agrave; đầy bất ngờ.</p>\r\n\r\n<p>Phong c&aacute;ch viết sắc sảo, l&ocirc;i cuốn v&agrave; tinh tế.</p>\r\n\r\n<p>T&aacute;c giả Higashino Keigo l&agrave; một trong những nh&agrave; văn trinh th&aacute;m h&agrave;ng đầu.</p>\r\n', '<p>T&ocirc;i từng xem những kẻ ngoại t&igrave;nh l&agrave; kh&ocirc;ng ra g&igrave;. O&aacute;i oăm thay, t&ocirc;i phải n&oacute;i lời đ&oacute; với ch&iacute;nh m&igrave;</p>\r\n\r\n<p>Watanabe l&agrave;m việc cho một c&ocirc;ng ty x&acirc;y dựng, đang bước v&agrave;o tuổi trung ni&ecirc;n v&agrave; cảm thấy cuộc sống gia đ&igrave;nh nh&agrave;m ch&aacute;n tẻ nhạt, th&igrave; bỗng phải l&ograve;ng Nakanishi Akiha, một nh&acirc;n vi&ecirc;n thời vụ mới đến l&agrave;m c&ugrave;ng ph&ograve;ng. Mối quan hệ giữa hai người nhanh ch&oacute;ng trở n&ecirc;n s&acirc;u sắc v&agrave; Watanabe biết được ho&agrave;n cảnh phức tạp m&agrave; Akiha đang phải đối mặt. Mười lăm năm trước đ&atilde; xảy ra một vụ &aacute;n mạng, Akiha l&agrave; nghi phạm. Liệu người t&igrave;nh dấu y&ecirc;u của m&igrave;nh c&oacute; phải thủ phạm thực sự kh&ocirc;ng? Tr&aacute;i tim của Watanabe dao động. Thời hiệu truy cứu của vụ &aacute;n sắp hết rồ</p>\r\n\r\n<p>Higashino Keigo sinh năm 1958 tại Osaka, l&agrave; nh&agrave; văn trinh th&aacute;m h&agrave;ng đầu hiện nay tại Nhật. Năm 1985, Higashino Keigo gi&agrave;nh Giải Edogawa Rampo lần thứ 31 cho t&aacute;c phẩm trinh th&aacute;m hay nhất với tiểu thuyết Giờ tan học.</p>\r\n\r\n<p>C&aacute;c năm sau đ&oacute;, Higashino Keigo li&ecirc;n tục được đề cử v&ocirc; số c&aacute;c giải thưởng văn học lớn. Năm 1999, &ocirc;ng đoạt giải Mystery Writers of Japan Inc với tiểu thuyết B&iacute; mật của Naoko, v&agrave; năm 2006, l&agrave; giải Naoki lần thứ 134 cho Ph&iacute;a sau nghi can X.</p>\r\n\r\n<p>Phố tan m&agrave;n đ&ecirc;m được xuất bản lần đầu v&agrave;o năm 2007, được dựng th&agrave;nh phim điện ảnh năm 2011.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 39),
(116, 'Giấc Mơ Tiên Tri', '139', '82700', 43, '1715108242_50.jpg.webp', '<p>C&acirc;u chuyện hấp dẫn v&agrave; g&acirc;y cấn, với yếu tố trinh th&aacute;m huyền b&iacute;.</p>\r\n\r\n<p>Higashino Keigo l&agrave; nh&agrave; văn trinh th&aacute;m nổi tiếng, đảm bảo chất lượng.</p>\r\n\r\n<p>Nh&atilde; Nam l&agrave; c&ocirc;ng ty ph&aacute;t h&agrave;nh uy t&iacute;n, đảm bảo h&agrave;ng ch&iacute;nh h&atilde;ng.</p>\r\n', '<p>Đ&ecirc;m khuya, một g&atilde; đ&agrave;n &ocirc;ng lẻn v&agrave;o ph&ograve;ng của thiếu nữ mười s&aacute;u tuổi. Người mẹ ph&aacute;t hiện v&agrave; nổ s&uacute;ng. Khi bị bắt, g&atilde; đ&agrave;n &ocirc;ng khai hắn đ&atilde; mơ thấy m&igrave;nh trở th&agrave;nh chồng thiếu nữ n&agrave;y từ mười bảy năm về trước, bằng chứng l&agrave; b&agrave;i văn m&ocirc; tả c&ocirc; g&aacute;i do hắn viết từ thời tiểu học. Lẽ n&agrave;o người trong mơ lại xuất hiện ngo&agrave;i đời thực? Đ&oacute; chỉ l&agrave; sự tr&ugrave;ng hợp qu&aacute; đỗi ngẫu nhi&ecirc;n, hay thực sự tồn tại giấc mơ ti&ecirc;n tri?</p>\r\n\r\n<p>Một lần nữa, nh&agrave; vật l&yacute; học thi&ecirc;n t&agrave;i Yukawa buộc phải ra tay, ph&aacute; giải h&agrave;ng loạt vụ &aacute;n nhuốm m&agrave;u huyền b&iacute;</p>\r\n\r\n<p>Higashino Keigo sinh năm 1958 tại Osaka, l&agrave; nh&agrave; văn trinh th&aacute;m h&agrave;ng đầu hiện nay tại Nhật. Năm 1985, Higashino Keigo gi&agrave;nh Giải Edogawa Rampo lần thứ 31 cho t&aacute;c phẩm trinh th&aacute;m hay nhất với tiểu thuyết Giờ tan học.</p>\r\n\r\n<p>C&aacute;c năm sau đ&oacute;, Higashino Keigo đ&atilde; nhận được nhiều giải thưởng văn học. Năm 1999, &ocirc;ng đoạt giải Mystery Writers of Japan Inc với tiểu thuyết B&iacute; mật của Naoko, v&agrave; năm 2006, l&agrave; giải Naoki lần thứ 134 cho Ph&iacute;a sau nghi can X.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 39);
INSERT INTO `tbl_sanpham` (`id_sanpham`, `tensanpham`, `masp`, `giasp`, `soluong`, `hinhanh`, `tomtat`, `noidung`, `tinhtrang`, `id_danhmuc`) VALUES
(117, 'Nàng Thợ May ', '140', '103000', 36, '1715108621_51.jpg.webp', '<p>C&acirc;u chuyện đầy cảm x&uacute;c v&agrave; sự can đảm của nh&acirc;n vật ch&iacute;nh.</p>\r\n\r\n<p>Ng&ocirc;n ngữ s&acirc;u sắc, th&acirc;n thiện v&agrave; gần gũi.</p>\r\n\r\n<p>Minh họa bởi Takeoka Miho, minh họa cho nhiều t&aacute;c phẩm Light Novel nổi tiếng.</p>\r\n', '<p>V&agrave;i bộ quần &aacute;o cũ, một b&eacute; nhện v&agrave; một c&acirc;y kim kh&acirc;u. Đ&oacute; l&agrave; tất cả những g&igrave; t&ocirc;i c&oacute; v&agrave;o năm mười lăm tuổi.</p>\r\n\r\n<p>Kiếp trước, t&ocirc;i l&agrave; Tsumugi.</p>\r\n\r\n<p>Kiếp n&agrave;y, t&ocirc;i l&agrave; Yui.</p>\r\n\r\n<p>Neil Yui.</p>\r\n\r\n<p>T&ocirc;i vốn sinh ra tại Nhật Bản, một đất nước nằm ở Tr&aacute;i Đất, nhưng được đầu thai đến thế giới ma thuật với to&agrave;n bộ k&yacute; ức của năm mười t&aacute;m tuổi.</p>\r\n\r\n<p>Từ khi mới chỉ l&agrave; một đứa trẻ sơ sinh chẳng hay biết về sự đặc biệt của gia tộc m&igrave;nh, thứ khiến t&ocirc;i nhận ra đ&acirc;y l&agrave; thế giới ma thuật ch&iacute;nh l&agrave; sự tồn tại của c&aacute;c tinh linh. Tinh linh đầu ti&ecirc;n t&ocirc;i tr&ocirc;ng thấy nhỏ x&iacute;u, mặc một chiếc v&aacute;y r&aacute;ch tả tơi, tr&ecirc;n cơ thể đầy những vết thương v&agrave; l&uacute;c n&agrave;o cũng kh&oacute;c th&uacute;t th&iacute;t. Thi thoảng t&ocirc;i lại bắt gặp c&ocirc; ấy. Sau khi chuyển kiếp đến thế giới n&agrave;y, t&ocirc;i đột nhi&ecirc;n nh&igrave;n thấy những tồn tại kh&ocirc;ng tưởng như thế. Chưa hết, t&ocirc;i c&ograve;n c&oacute; thể điều khiển thứ rất c&oacute; khả năng l&agrave; ma lực của t&ocirc;i sau khi thử nghiệm nhiều lần.</p>\r\n\r\n<p>Cuộc sống của một đứa trẻ sơ sinh thật nh&agrave;m ch&aacute;n. Nhất l&agrave; với t&ocirc;i, người đ&atilde; c&oacute; mười t&aacute;m năm kinh nghiệm cuộc đời ngay từ l&uacute;c lọt l&ograve;ng. M&agrave; kh&ocirc;ng, c&oacute; lẽ t&ocirc;i đ&atilde; nhận thức được xung quanh từ khi nằm trong bụng mẹ ấy chứ. Kh&ocirc;ng hiểu sao, t&ocirc;i cứ mang m&aacute;ng nhớ rằng m&igrave;nh từng nằm ngủ mơ m&agrave;ng ở một nơi n&agrave;o đ&oacute;. T&ocirc;i đ&atilde; qu&ecirc;n gần hết những chuyện xảy ra trong khoảng thời gian mới đầu thai, nhưng k&yacute; ức về kiếp trước lại c&ograve;n nguy&ecirc;n vẹn. Bởi lẽ đ&oacute;, qu&atilde;ng thời gian l&agrave;m em b&eacute; của t&ocirc;i v&ocirc; c&ugrave;ng buồn ch&aacute;n v&agrave; khốn khổ. Ngay cả khi chưa biết ng&ocirc;n ngữ của thế giới n&agrave;y, t&ocirc;i vẫn dễ d&agrave;ng hiểu được rằng m&igrave;nh chẳng hề nhận được ch&uacute;t t&igrave;nh thương n&agrave;o từ những người c&oacute; vẻ l&agrave; hầu g&aacute;i đang tr&ocirc;ng nom m&igrave;nh. Dưới sự chăm s&oacute;c lấy lệ của họ, l&uacute;c n&agrave;o t&ocirc;i cũng phải g&agrave;o kh&oacute;c đến khản giọng chỉ để được thay t&atilde; v&agrave; cho ăn. Hầu hết thời gian t&ocirc;i đều bị bỏ mặc, vậy n&ecirc;n t&ocirc;i biết n&oacute;i rất muộn.</p>\r\n\r\n<p>Th&agrave;nh thật m&agrave; n&oacute;i, ngay cả khi đ&atilde; nhận ra nơi n&agrave;y l&agrave; thế giới ma thuật nhờ nh&igrave;n thấy tinh linh v&agrave; ma lực, th&igrave; cũng phải rất l&acirc;u về sau t&ocirc;i mới biết đ&acirc;y l&agrave; một gia đ&igrave;nh qu&yacute; tộc.</p>\r\n\r\n<p>Đồ nội thất trong ph&ograve;ng mang phong c&aacute;ch ch&acirc;u &Acirc;u thời Trung cổ m&agrave; t&ocirc;i từng thấy tr&ecirc;n ti vi v&agrave;o kiếp trước, nhưng v&igrave; kh&ocirc;ng được lau dọn v&agrave; bảo dưỡng cẩn thận n&ecirc;n tất cả đều cũ kỹ c&aacute;u bẩn. Giấy d&aacute;n tường hẳn từng c&oacute; m&agrave;u trắng nay đ&atilde; ố v&agrave;ng v&agrave; bong tr&oacute;c nhiều chỗ, r&egrave;m cửa th&igrave; nặng nề v&agrave; b&aacute;m đầy bụi bặm như chưa được giặt giũ trong nhiều năm. Ph&ograve;ng trẻ em m&agrave; thế đấy. D&ugrave; c&oacute; hầu g&aacute;i đi chăng nữa, đ&acirc;y hẳn l&agrave; một gia đ&igrave;nh thương nh&acirc;n l&agrave;m ăn lụn bại. T&ocirc;i cứ đinh ninh như vậy, cho tới khi vị gia sư (người ở b&ecirc;n t&ocirc;i đến năm t&ocirc;i mười tuổi) dạy cho t&ocirc;i ng&ocirc;n ngữ của thế giới n&agrave;y.</p>\r\n', 1, 37),
(118, 'Chiếc Hộp Giáng Sinh', '141', '46100', 27, '1715108753_52.jpg.webp', '', '<p>Gi&aacute;ng sinh đ&atilde; về tr&ecirc;n th&agrave;nh phố. Những cơn gi&oacute; th&aacute;ng Mười hai buốt gi&aacute;, tuyết rơi d&agrave;y v&agrave; ngọn lửa trong l&ograve; sưởi đ&atilde; rực l&ecirc;n ấm n&oacute;ng. Trong căn &aacute;p m&aacute;i một ng&ocirc;i biệt thự tr&ecirc;n khu Đại Lộ th&agrave;nh phố Salt Lake, những bức thư ngả v&agrave;ng nằm đ&oacute; mấy chục năm, chờ được đọc l&ecirc;n lần nữa. Những l&aacute; thư b&iacute; ẩn khiến người ta rơi lệ, bởi nỗi đau song h&agrave;nh c&ugrave;ng t&igrave;nh y&ecirc;u tha thiết của một người mẹ mất con. V&agrave; nhờ đ&oacute;, &yacute; nghĩa của Chiếc hộp Gi&aacute;ng Sinh đựng những bức thư, của ng&agrave;y Gi&aacute;ng sinh thi&ecirc;ng li&ecirc;ng đ&atilde; được h&eacute; lộ. Một cuốn s&aacute;ch như lời ngợi ca t&igrave;nh y&ecirc;u bất tử m&agrave; Thượng đế d&agrave;nh cho lo&agrave;i người, khi Người gửi con trai m&igrave;nh ra đi, dẫu biết con đường n&agrave;o chờ đợi ph&iacute;a trước.</p>\r\n', 1, 37),
(119, 'Một Lít Nước Mắt', '142', '79350', 9, '1715109128_53.jpg.webp', '<p>C&acirc;u chuyện cảm động về sự can đảm v&agrave; hy vọng trong cuộc sống của Kitou Aya.</p>\r\n\r\n<p>Ng&ocirc;n ngữ s&acirc;u sắc, th&acirc;n thiện v&agrave; gần gũi, gi&uacute;p độc giả hiểu v&agrave; đồng cảm với nh&acirc;n vật ch&iacute;nh.</p>\r\n\r\n<p>Chuyển thể th&agrave;nh phim hai lần v&agrave; cảm động h&agrave;ng triệu người xem.</p>\r\n', '<p>Căn bệnh Tho&aacute;i h&oacute;a tiểu n&atilde;o ph&aacute;t triển khiến Aya phải ngồi xe lăn, kh&ocirc;ng ph&aacute;t &acirc;m được như &yacute; muốn, kh&ocirc;ng thể cầm đũa v&agrave; cuối c&ugrave;ng l&agrave; nằm liệt giường. Aya kể lại cuộc chiến dai dẳng h&agrave;ng năm trời với căn bệnh hiểm ngh&egrave;o qua những d&ograve;ng nhật k&yacute; đẫm nước mắt.</p>\r\n\r\n<p>Cảm động trước nghị lực sống phi thường của c&ocirc; g&aacute;i b&eacute; nhỏ. Họ tr&acirc;n qu&yacute; cuộc sống như thế đ&oacute;, c&ograve;n ch&uacute;ng ta th&igrave; sao?</p>\r\n\r\n<p><em>&ldquo;H&atilde;y sống! M&igrave;nh muốn h&iacute;t thở thật s&acirc;u dưới trời xanh.&rdquo;</em></p>\r\n\r\n<p><em>Một t&acirc;m hồn nhạy cảm.</em></p>\r\n\r\n<p><em>Một gia đ&igrave;nh ấm &aacute;p.</em></p>\r\n\r\n<p><em>Một căn bệnh hiểm ngh&egrave;o.</em></p>\r\n\r\n<p><em>Một cơ thể tật nguyền.</em></p>\r\n\r\n<p>Đ&oacute; l&agrave; những g&igrave; Kitou Aya c&oacute; trong hơn 20 năm cuộc đời. Với Aya, tương lai của c&ocirc; l&agrave; một con đường hẹp, v&agrave; c&agrave;ng ng&agrave;y n&oacute; c&agrave;ng trở n&ecirc;n hẹp hơn. Căn bệnh ngăn trở Aya khỏi tất cả những ước mơ v&agrave; dự định, thậm ch&iacute; việc tự m&igrave;nh bước ra ngo&agrave;i phố để đi tới hiệu s&aacute;ch cũng trở th&agrave;nh một khao kh&aacute;t ch&aacute;y bỏng. Hơn 6 năm ki&ecirc;n tr&igrave; viết nhật k&yacute;, c&ocirc; kể về những cảm nhận v&agrave; suy tư của bản th&acirc;n trong suốt qu&atilde;ng thời gian chứng kiến cơ thể m&igrave;nh từng bước từng bước g&aacute;nh lấy một số phận đau đớn . Nhưng từ trong nước mắt v&agrave; tật nguyền, cuộc t&igrave;m kiếm gi&aacute; trị bản th&acirc;n của c&ocirc; đ&atilde; l&agrave;m r&uacute;ng động cả Nhật Bản.</p>\r\n\r\n<p>&ldquo;C&oacute; những người m&agrave; sự tồn tại của họ giống như kh&ocirc;ng kh&iacute;, &ecirc;m dịu, nhẹ nh&agrave;ng, chỉ khi họ mất đi người ta mới nhận ra họ quan trọng nhường n&agrave;o. M&igrave;nh muốn trở th&agrave;nh một sự tồn tại như thế.&rdquo;</p>\r\n\r\n<p>- Tr&iacute;ch Một l&iacute;t nước mắt</p>\r\n\r\n<p>Bước v&agrave;o tuổi 15, một căn bệnh mang t&ecirc;n Tho&aacute;i h&oacute;a tiểu n&atilde;o (Spinocerebellar Atrophy) bỗng gi&aacute;ng xuống đầu Kitou Aya (19/07/1962 &ndash; 23/05/1988). Căn bệnh ph&aacute;t triển khiến Aya dần mất đi khả năng kiểm so&aacute;t cơ thể. Theo thời gian Aya phải ngồi xe lăn, kh&ocirc;ng ph&aacute;t &acirc;m được như &yacute; muốn, kh&ocirc;ng thể cầm đũa, rồi cuối c&ugrave;ng l&agrave; nằm liệt giường. Aya kể lại cuộc chiến dai dẳng h&agrave;ng năm trời với căn bệnh hiểm ngh&egrave;o qua những d&ograve;ng nhật k&yacute; đẫm nước mắt. Năm 1986, nhật k&yacute; của Aya lần đầu được ph&aacute;t h&agrave;nh th&agrave;nh s&aacute;ch với t&ecirc;n gọi Một l&iacute;t nước mắt, được dựng th&agrave;nh phim hai lần, v&agrave; khiến h&agrave;ng triệu người xem cảm động rơi lệ. Từ đ&oacute; đến nay chỉ ri&ecirc;ng ở Nhật Bản cuốn s&aacute;ch đ&atilde; b&aacute;n được hơn 1.1 triệu bản</p>\r\n\r\n<p><em>&ldquo;Vấp ng&atilde; ư? Chẳng vấn đề.</em></p>\r\n\r\n<p><em>D&ugrave; thế n&agrave;o ta vẫn c&oacute; thể đứng l&ecirc;n.</em></p>\r\n\r\n<p><em>L&uacute;c vấp ng&atilde; h&atilde;y ngước l&ecirc;n nh&igrave;n trời kia.</em></p>\r\n\r\n<p><em>Bầu trời xanh bao la ng&uacute;t ng&agrave;n tầm mắt.</em></p>\r\n\r\n<p><em>C&oacute; thấy n&oacute; đang mỉm cười với bạn kh&ocirc;ng?</em></p>\r\n\r\n<p><em>Bạn đang c&ograve;n sống.&rdquo;</em></p>\r\n\r\n<p>- Tr&iacute;ch Một l&iacute;t nước mắt</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 37),
(120, 'Phân Tâm Học', '143', '104000', 47, '1715109914_54.jpg.webp', '<p>Giới thiệu l&yacute; thuyết Ph&acirc;n t&acirc;m học của Freud, một nh&agrave; t&acirc;m l&yacute; học nổi tiếng.</p>\r\n\r\n<p>Đưa ra những kh&aacute;i niệm mới lạ v&agrave; độc đ&aacute;o như kho&aacute;i lạc, v&ocirc; thức.</p>\r\n\r\n<p>Ảnh hưởng s&acirc;u sắc đến tư duy hiện đại v&agrave; nhiều lĩnh vực đời sống.</p>\r\n', '<p>Khi khai sinh l&yacute; thuyết Ph&acirc;n t&acirc;m học g&acirc;y sốc với c&aacute;c kh&aacute;i niệm mới lạ như nguy&ecirc;n tắc kho&aacute;i lạc, libido, hay khi đặt v&ocirc; thức v&agrave;o trung t&acirc;m nh&acirc;n c&aacute;ch, v&ocirc; h&igrave;nh trung vạch ra ch&acirc;n tướng tăm tối của l&ograve;ng người, Sigmund Freud vừa được ngợi ca vừa bị chỉ tr&iacute;ch gay gắt. Suốt thế kỷ 20 sang 21, học thuyết của &ocirc;ng thổi luồng sinh kh&iacute; mạnh mẽ, đ&oacute;ng g&oacute;p v&ocirc; song v&agrave;o tư duy hiện đại, v&agrave;o sự chi&ecirc;m nghiệm suy tư, ảnh hưởng s&acirc;u rộng v&agrave; chan h&ograve;a đến mọi lĩnh vực đời sống lẫn khoa học v&agrave; nghệ thuật như triết học, t&acirc;m l&yacute; học, x&atilde; hội học, y học v&agrave; văn chươ</p>\r\n\r\n<p>V&igrave; sao Sigmund Freud vừa được t&ocirc;n vinh như t&acirc;m l&yacute; gia thi&ecirc;n t&agrave;i, triết gia lớn của thời đại, lại vừa bị coi l&agrave; kẻ ph&aacute; bĩnh, lừa bịp nhất trong lịch sử tư tưởng nh&acirc;n loại? &Ocirc;ng đ&atilde; thực sự n&oacute;i g&igrave;? Độc giả c&oacute; thể t&igrave;m thấy c&acirc;u trả lời cụ thể ở một trước t&aacute;c quan trọng m&agrave; &ocirc;ng tr&igrave;nh b&agrave;y những điểm ch&iacute;nh yếu trong học thuyết của m&igrave;nh: Nghi&ecirc;n cứu ph&acirc;n t&acirc;m học.</p>\r\n', 1, 47),
(121, 'Cẩm Nang Nghiên Cứu', '144', '130000', 19, '1715110032_55.jpg.webp', '<p>Hướng dẫn chi tiết từ &yacute; tưởng đến c&ocirc;ng bố.</p>\r\n\r\n<p>V&iacute; dụ thực tế gi&uacute;p &aacute;p dụng kiến thức v&agrave;o thực tế.</p>\r\n\r\n<p>Viết dễ hiểu, gần gũi v&agrave; hữu &iacute;ch cho người mới nghi&ecirc;n cứu.</p>\r\n', '<p>Cuốn s&aacute;ch &ldquo;Cẩm nang nghi&ecirc;n cứu khoa học từ &yacute; tưởng đến c&ocirc;ng bố&rdquo; được soạn ra để gi&uacute;p cho c&aacute;c bạn muốn dấn th&acirc;n v&agrave;o c&ocirc;ng việc nghi&ecirc;n cứu khoa học. Nhiều học sinh v&agrave; sinh vi&ecirc;n thường tự hỏi bắt đầu nghi&ecirc;n cứu khoa học từ đ&acirc;u, &yacute; tưởng nghi&ecirc;n cứu xuất ph&aacute;t từ nguồn n&agrave;o, phương ph&aacute;p nghi&ecirc;n cứu ra sao, v&agrave; c&ocirc;ng bố kết quả ngi&ecirc;n cứu ở đ&acirc;u. Đ&oacute; l&agrave; những c&acirc;u hỏi cơ bản trong qui tr&igrave;nh nghi&ecirc;n cứu khoa học. Qua 21 chương s&aacute;ch, t&aacute;c giả sẽ giải đ&aacute;p những c&acirc;u hỏi tr&ecirc;n v&agrave; k&egrave;m theo những v&iacute; dụ thực tế gi&uacute;p bạn đọc hiểu r&otilde; hơn về qui tr&igrave;nh nghi&ecirc;n cứu khoa học.<br />\r\n&ldquo;Khoa học kh&ocirc;ng bao giờ dừng lại trong h&agrave;nh tr&igrave;nh đi t&igrave;m sự thật, bởi v&igrave; khoa học kh&ocirc;ng bao giờ tuy&ecirc;n bố n&oacute; đ&atilde; ho&agrave;n th&agrave;nh sứ mệnh đ&oacute;. Khoa học l&agrave; một ngh&agrave;nh nghề khai h&oacute;a, truyền b&aacute; c&aacute;i m&agrave; ch&uacute;ng ta gọi l&agrave; &ldquo;văn minh&rdquo;, v&agrave; khoa học đặt sự thật tr&ecirc;n hết v&agrave; trước hết, trước tất cả những quyền lợi c&aacute; nh&acirc;n.&rdquo;</p>\r\n', 1, 47),
(122, 'Nghiên Cứu Bản Đồ', '145', '75220', 23, '1715110247_56.jpg.webp', '<p>Kh&aacute;m ph&aacute; về địa l&yacute;, địa mạo v&agrave; c&aacute;c cửa s&ocirc;ng, hải cảng của Việt Nam thế kỷ XV.</p>\r\n\r\n<p>Hiểu r&otilde; sự thay đổi của c&aacute;c c&ocirc;ng tr&igrave;nh nh&acirc;n tạo v&agrave; địa danh trong suốt ba thế kỷ.</p>\r\n\r\n<p>Được trải nghiệm một c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu chi tiết v&agrave; đ&aacute;ng tin cậy về lịch sử địa phương.</p>\r\n', '<p>C&ocirc;ng tr&igrave;nh&nbsp;<strong>Nghi&ecirc;n cứu bản đồ c&aacute;c cửa s&ocirc;ng, hải cảng Việt Nam thế kỷ XV</strong>&nbsp;(nguy&ecirc;n t&aacute;c: &Eacute;tude sur un Portulan annamite du XVe si&egrave;cle) của học giả người Ph&aacute;p Gustave Dumoutier viết bằng tiếng Ph&aacute;p, thực hiện xong th&aacute;ng 8 năm 1895. To&agrave;n bộ c&ocirc;ng tr&igrave;nh đăng tải tr&ecirc;n tạp ch&iacute; Địa l&yacute; lịch sử v&agrave; m&ocirc; tả (Bulletin de g&eacute;ographie historique et descriptive), số 2 năm 1896, được đ&aacute;nh gi&aacute; cao v&agrave; t&aacute;c giả được giải thưởng &ldquo;Jomard&rdquo; của Hiệp hội Địa l&yacute; năm 1897.&nbsp;</p>\r\n\r\n<p>C&ocirc;ng tr&igrave;nh&nbsp;Nghi&ecirc;n cứu bản đồ c&aacute;c cửa s&ocirc;ng, hải cảng Việt Nam thế kỷ XV, sau đ&acirc;y gọi tắt l&agrave; Tập Hải đồ, gồm một tập 25 tờ ch&uacute; th&iacute;ch v&agrave; 24 tấm bản đồ nối tiếp từ Kinh th&agrave;nh Thăng Long đến cố đ&ocirc; Vương quốc Champa c&ugrave;ng với c&aacute;c phụ lục l&agrave; hai H&agrave;nh tr&igrave;nh đường bộ v&agrave; H&agrave;nh tr&igrave;nh đường thủy m&agrave; t&aacute;c giả cho biết: &ldquo;Đ&acirc;y l&agrave; một t&agrave;i liệu qu&acirc;n sự, được thiết lập dựa tr&ecirc;n c&aacute;c th&ocirc;ng tin thu thập được khoảng cuối thế kỷ XV của c&aacute;c ph&aacute;i vi&ecirc;n do vua L&ecirc; Th&aacute;nh T&ocirc;ng cử đi mật th&aacute;m để vẽ chuẩn bị cho việc th&ocirc;n t&iacute;nh Champa được thuận lợi.&rdquo;</p>\r\n\r\n<p>Với 24 tấm bản đồ v&agrave; phụ lục k&egrave;m theo viết bằng chữ H&aacute;n hoặc N&ocirc;m, G. Dumoutier đ&atilde; thực hiện: sao, chụp lại c&aacute;c tấm bản đồ; đọc v&agrave; phi&ecirc;n &acirc;m c&aacute;c địa danh, c&aacute;c chỉ dẫn ra chữ Quốc ngữ v&agrave; giải nghĩa bằng tiếng Ph&aacute;p, đ&aacute;nh số ngay s&aacute;t c&aacute;c chữ H&aacute;n, hoặc N&ocirc;m tr&ecirc;n bản đồ những con số tương ứng để người đọc dễ đối chiếu; chuyển ngữ từ H&aacute;n, hoặc N&ocirc;m ra chữ Quốc ngữ v&agrave; giải nghĩa bằng tiếng Ph&aacute;p cả hai H&agrave;nh tr&igrave;nh đường bộ v&agrave; H&agrave;nh tr&igrave;nh đường thủy (s&ocirc;ng, biển); cuối c&ugrave;ng, nghi&ecirc;n cứu, phụ bổ c&aacute;c ch&uacute; giải địa l&yacute;, lịch sử v&agrave; khảo cổ tr&ecirc;n c&aacute;c v&ugrave;ng đất m&agrave; G. Dumoutier đ&atilde; đi qua tr&ecirc;n hai h&agrave;nh tr&igrave;nh bằng việc đối chiếu Tập Hải đồ với c&aacute;c bản đồ đương đại (cuối thế kỷ XIX). Qua việc nghi&ecirc;n cứu bằng ph&eacute;p đối chiếu bản đồ v&agrave; đi thực tế, t&aacute;c giả đ&atilde; n&ecirc;u l&ecirc;n những thay đổi kh&aacute; quan trọng từ ba thế kỷ tại một số điểm miền duy&ecirc;n hải Trung kỳ v&agrave; Bắc kỳ so với những trầm t&iacute;ch của c&aacute;c d&ograve;ng s&ocirc;ng.</p>\r\n\r\n<p>Dumoutier chia Tập Hải đồ l&agrave;m bốn phần:&nbsp;</p>\r\n\r\n<p>Phần I - Dịch v&agrave; ch&uacute; giải c&aacute;c t&agrave;i liệu, phụ lục k&egrave;m theo Tập Hải đồ gồm đường h&agrave;nh qu&acirc;n từ Thăng Long đến kinh đ&ocirc; của Champa (H&agrave;nh tr&igrave;nh đường bộ) v&agrave; đường s&ocirc;ng, đường biển c&ugrave;ng c&aacute;c chỉ dẫn h&agrave;ng hải (H&agrave;nh tr&igrave;nh đường thủy). V&igrave; vậy, c&ocirc;ng tr&igrave;nh n&agrave;y kh&ocirc;ng chỉ cho ta biết địa h&igrave;nh, địa mạo, đặc điểm bờ biển Việt Nam v&agrave; c&aacute;c cửa s&ocirc;ng, hải cảng từ tỉnh Thanh H&oacute;a đến tỉnh B&igrave;nh Thuận tr&ecirc;n Tập Hải đồ m&agrave; c&ograve;n cho biết rất r&otilde; từng cung đường, kho, xưởng, mỏ quặng, n&uacute;i, s&ocirc;ng ở c&aacute;c l&agrave;ng x&atilde; v&ugrave;ng đồng bằng v&agrave; cả nhiều ngả miền n&uacute;i kh&aacute;c nhau của nước Đại Việt.</p>\r\n\r\n<p>Phần II - Phi&ecirc;n &acirc;m, dịch, giải nghĩa c&aacute;c địa danh v&agrave; c&aacute;c chỉ dẫn được ghi tr&ecirc;n từng tờ bản đồ. Đ&acirc;y l&agrave; phần l&yacute; th&uacute; v&igrave; G. Dumoutier đ&atilde; dịch v&agrave; giải nghĩa c&aacute;c địa danh, danh xưng tr&ecirc;n mỗi bản đồ v&agrave; lập n&ecirc;n một bảng tương ứng với c&aacute;c số đ&atilde; được đ&aacute;nh thứ tự để người xem dễ đối chiếu v&agrave; tiện theo d&otilde;i. Trong phần n&agrave;y t&aacute;c giả đ&atilde; lập 25 bảng ch&uacute; th&iacute;ch (đ&aacute;nh số La-tinh) tương ứng với 25 bản đồ (đ&aacute;nh số La-m&atilde;).&nbsp;</p>\r\n\r\n<p>Phần III - Phần khảo cứu của G. Dumoutier, với phương ph&aacute;p đối chiếu so s&aacute;nh c&aacute;c cửa s&ocirc;ng, hải cảng tr&ecirc;n Tập Hải đồ với c&aacute;c bản đồ đương thời (cuối thế kỷ XIX) m&agrave; t&aacute;c giả gọi l&agrave; Bản đồ Bộ Tham mưu c&ugrave;ng với c&aacute;c cuộc du khảo thực tế. Nội dung phần khảo cứu n&agrave;y, t&aacute;c giả đ&atilde; n&ecirc;u l&ecirc;n những đổi thay kh&aacute; quan trọng từ c&aacute;c c&ocirc;ng tr&igrave;nh nh&acirc;n tạo như th&agrave;nh lũy ở H&agrave; Nội, những đoạn đ&ecirc; phải đắp lại do lũ lụt, những ng&ocirc;i đền, ch&ugrave;a v&agrave; c&aacute;c chiến lũy xưa kia chỉ c&ograve;n dấu t&iacute;ch, cả những con s&ocirc;ng đ&atilde; chuyển dịch theo thời gian, hay bờ biển bị x&acirc;m lấ</p>\r\n\r\n<p>Phần IV - Một tập gồm 24 tờ bản đồ c&oacute; đ&aacute;nh số La-m&atilde; từ I đến XXIV, đ&iacute;nh k&egrave;m ri&ecirc;ng biệt, c&oacute; ghi bằng chữ H&aacute;n, hoặc N&ocirc;m. Mặc d&ugrave; những bản đồ n&agrave;y l&agrave;m cơ sở ch&iacute;nh v&agrave; l&agrave; xương cốt ch&iacute;nh cho c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu c&aacute;c cửa s&ocirc;ng, hải cảng Việt Nam ở thế kỷ XV của G. Dumoutier nhưng tr&ecirc;n đ&oacute;, kh&ocirc;ng chỉ c&oacute; c&aacute;c cửa s&ocirc;ng, hải cảng của nước Đại Việt dọc từ bắc miền Trung đến Tr&agrave; B&agrave;n, cố đ&ocirc; Champa (tỉnh Ninh Thuận ng&agrave;y nay) m&agrave; c&ograve;n c&oacute; trục đường c&aacute;i quan l&agrave;m điểm nhận biết v&agrave; rất nhiều địa danh, t&ecirc;n gọi của c&aacute;c l&agrave;ng x&atilde; xưa kia, c&ugrave;ng rất nhiều th&ocirc;ng tin như kho, xưởng, mỏ quặng, s&ocirc;ng, n&uacute; của nước Đại Việt.</p>\r\n', 1, 47),
(123, 'Kỹ Thuật Giao Thông', '146', '60000', 29, '1715110382_57.jpg.webp', '<p>Cung cấp kiến thức cơ bản v&agrave; n&acirc;ng cao về kỹ thuật giao th&ocirc;ng.</p>\r\n\r\n<p>Đưa ra v&iacute; dụ v&agrave; c&acirc;u hỏi gi&uacute;p hiểu s&acirc;u hơn về c&aacute;c vấn đề giao th&ocirc;ng.</p>\r\n\r\n<p>Được bi&ecirc;n soạn dựa tr&ecirc;n c&aacute;c cuốn s&aacute;ch kinh điển v&agrave; kiến thức nghi&ecirc;n cứu của t&aacute;c giả trong nhiều năm.</p>\r\n', '<p>Đất nước ta đang trong thời kỳ ph&aacute;t triển mạnh mẽ tr&ecirc;n nhiều lĩnh vực, đặc biệt l&agrave; kinh tế. Sự ph&aacute;t triển đ&oacute; tạo &aacute;p lực rất lớn đến hệ thống giao th&ocirc;ng đường bộ v&agrave; v&agrave; tạo c&aacute;c vấn đề nghi&ecirc;m trọng của giao th&ocirc;ng m&agrave; h&agrave;ng ng&agrave;y mỗi người ch&uacute;ng ta, kh&ocirc;ng &iacute;t th&igrave; nhiều phải đối mặt. Cuốn s&aacute;ch l&agrave; m&ocirc;n khoa học quan trọng Kỹ thuật giao th&ocirc;ng khoa học quan trọng tạo c&ocirc;ng cụ cho c&aacute;c kỹ sư giao th&ocirc;ng, c&aacute;c nh&agrave; quản l&yacute;, c&aacute;c nh&agrave; khoa học giải quyết c&aacute;c vấn đề của giao th&ocirc;ng như &ugrave;n tắc, tai nạn giao th&ocirc;ng, &ocirc; nhiễm m&ocirc;i trường v&agrave; g&igrave;n giữ quỹ đất, X&aacute;c định được tầm quan trọng của m&ocirc;n khoa học n&agrave;y, t&aacute;c giả đ&atilde; tiến h&agrave;nh bi&ecirc;n soạn cuốn s&aacute;ch Kỹ thuật giao th&ocirc;ng nhằm gi&uacute;p sinh vi&ecirc;n c&aacute;c ng&agrave;nh cầu đường trong cả nước c&oacute; một c&ocirc;ng cụ hỗ trợ đắc lực trong học tập, gi&uacute;p những người l&agrave;m kỹ thuật, quản l&yacute; c&oacute; một t&agrave;i liệu tra cứu khi cần thiết. Do khối lượng kiến thức nhiều, cuốn s&aacute;ch n&agrave;y được chia định hướng r&otilde; r&agrave;ng theo hai tập:</p>\r\n\r\n<p>Tập 1: Nghi&ecirc;n cứu v&agrave; điều tra giao th&ocirc;ng. Tập 2: Ph&acirc;n t&iacute;ch giao th&ocirc;ng v&agrave; ứng dụng.</p>\r\n\r\n<p>Trong lần xuất bản n&agrave;y, t&aacute;c giả đ&atilde; cố gắng dựa tr&ecirc;n c&aacute;c cuốn s&aacute;ch kinh điển về kỹ thuật giao th&ocirc;ng tr&ecirc;n thế giới v&agrave; kiến thức của bản th&acirc;n trong nhiều năm nghi&ecirc;n cứu v&agrave; giảng dạy m&ocirc;n học n&agrave;y để cung cấp cho người đọc những kiến thức cơ bản nhất v&agrave; n&acirc;ng cao một phần nhằm phục vụ cho nhiều đối tượng bạn đọc. Th&ecirc;m v&agrave;o đ&oacute;, c&aacute;c nghi&ecirc;n cứu gần đ&acirc;y nhất v&agrave; c&aacute;c quy tr&igrave;nh mới nhất của Việt Nam, Mỹ (HCM, 2000, AASHTO, 2010), Canada, ch&acirc;u &Acirc;u được đưa v&agrave;o giới thiệu trong cuốn s&aacute;ch n&agrave;y. Rất nhiều v&iacute; dụ v&agrave; c&acirc;u hỏi được đưa ra nhằm gi&uacute;p người đọc hiểu s&acirc;u hơn c&aacute;c vấn đề.</p>\r\n\r\n<p>Do lĩnh vực chuy&ecirc;n ng&agrave;nh rộng, chắc chắn cuốn s&aacute;ch kh&ocirc;ng thể đ&aacute;p ứng hết c&aacute;c nhu cầu của người đọc v&agrave; sẽ kh&ocirc;ng tr&aacute;nh khỏi khiếm khuyết.</p>\r\n\r\n<p>T&aacute;c giả lu&ocirc;n mong muốn nhận được &yacute; kiến đ&oacute;ng g&oacute;p của bạn đọc, để c&aacute;c lần t&aacute;i bản sau s&aacute;ch được ho&agrave;n thiện hơn.</p>\r\n', 1, 47),
(124, 'Nghiên Cứu Xã Hội', '147', '137550', 41, '1715110551_58.jpg.webp', '<p>C&ocirc;ng ty ph&aacute;t h&agrave;nh : NXB Th&ocirc;ng Tin V&agrave; truyền Th&ocirc;ng&nbsp;</p>\r\n\r\n<p>Loại b&igrave;a : B&igrave;a mềm</p>\r\n\r\n<p>Số trang : 286</p>\r\n\r\n<p>Nh&agrave; xuất bản : Nh&agrave; Xuất Bản Th&ocirc;ng Tin V&agrave; Truyền Th&ocirc;ng</p>\r\n', '<p>Dư luận x&atilde; hội c&oacute; thể g&oacute;p phần x&acirc;y dựng hay ph&aacute; hủy danh tiếng của một tổ chức hay một x&atilde; hội, đưa ra y&ecirc;u cầu hoặc hủy bỏ c&aacute;c điều luật, x&acirc;y dựng hoặc b&agrave;o m&ograve;n, hủy hoại x&atilde; hội cũng như c&aacute;c chuẩn mực đạo đức, nhưng cũng c&oacute; thể tiếp th&ecirc;m sức mạnh hoặc l&agrave;m mất đi sinh kh&iacute;, tinh thần cộng đồng</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 47),
(125, 'Đá Quý Khoáng Sản', '148', '324350', 35, '1715110813_59.png.webp', '<p>Hơn 1000 mẫu vật đ&aacute; qu&yacute; v&agrave; kho&aacute;ng sản, đa dạng v&agrave; đẹp mắt.</p>\r\n\r\n<p>H&igrave;nh ảnh chụp cận cảnh, gi&uacute;p nhận dạng dễ d&agrave;ng.</p>\r\n\r\n<p>Th&ocirc;ng tin đầy đủ về ph&acirc;n loại, địa điểm, c&ocirc;ng thức h&oacute;a học, ứng dụng.</p>\r\n', '<p>- Cuốn s&aacute;ch đưa bạn v&agrave;o thế giới mu&ocirc;n m&agrave;u mu&ocirc;n dạng của c&aacute;c loại tinh thể, từ những kho&aacute;ng vật x&ugrave; x&igrave;, th&ocirc; r&aacute;p đến những loại đ&aacute; trong suốt, kh&ocirc;ng t&igrave; vết c&oacute; gi&aacute; trị khổng lồ.<br />\r\n- Với 1000+ mẫu vật về đ&aacute; qu&yacute; &amp; kh&oacute;ng sản<br />\r\n- Trong số 5000+ kho&aacute;ng chất được biết đến c&oacute; khoảng 130 loại đ&aacute; lấp l&aacute;nh m&agrave; ch&uacute;ng ra.<br />\r\n- Trong cuốn s&aacute;ch n&agrave;y, tập trung t&igrave;m hiểu 3 chủ đề ch&iacute;nh: Kho&aacute;ng sản - Đ&aacute; - Đ&aacute; Q, Mỗi chủ đề lại được ph&acirc;n t&aacute;ch v&agrave; đi v&agrave;o chi tiết của từng loại</p>\r\n\r\n<p>+ Mỗi trang s&aacute;ch đều như một thư viện h&igrave;nh ảnh thu nhỏ đầy m&agrave;u sắc<br />\r\n+ C&aacute;c th&ocirc;ng tin mới mẻ v&agrave; hấp dẫn đều được thể hiện dưới dạng infographic sinh động.<br />\r\n+ Mỗi loại kho&aacute;ng sản đều được cung cấp đầy đủ th&ocirc;ng tin từ ph&acirc;n loại nh&oacute;m &ndash; được t&igrave;m thấy ở đ&acirc;u, địa điểm ch&iacute;nh khu vực n&agrave;o &ndash; c&ocirc;ng thức h&oacute;a học &ndash; h&igrave;nh dạng &ndash; m&agrave;u sắc &ndash; kết cấu ch&iacute;nh &ndash; trọng lượng ri&ecirc;ng &ndash; cấu tr&uacute;c mạng tinh thể&nbsp;<br />\r\n+ Những h&igrave;nh ảnh được chụp cận cảnh k&egrave;m theo những sự thật bất ngờ<br />\r\n+ C&aacute;c bảng dữ liệu cần thiết cung cấp th&ocirc;ng tin địa chất v&agrave; những ứng dụng quen thuộc trong đời sống<br />\r\n+ Nhiều mẫu vật, h&igrave;nh ảnh để hỗ trợ nhận dạng c&aacute;c loại đ&aacute;<br />\r\n+ 99 Thuật ngữ ch&uacute; giải trải d&agrave;i tận 5 trang sẽ gi&uacute;p c&aacute;c bạn tra cứu những từ ngữ mang t&iacute;nh chuy&ecirc;n ng&agrave;nh.&nbsp;<br />\r\n+ 5 trang mục lục tra cứu thuật ngữ tiếng anh theo bảng anphabet tiện lợi.</p>\r\n\r\n<p>- Một cuốn s&aacute;ch tuyệt vời, khiến bạn sở hữu ngay để t&igrave;m kho b&aacute;u của ri&ecirc;ng m&igrave;nh v&agrave; tự x&acirc;y dựng bộ sưu tập của c&oacute; 1-0-2. M&oacute;n qu&agrave; tuyệt vời d&agrave;nh cho những bạn c&oacute; ch&uacute;t t&ograve; m&ograve; về thế giới xung quanh, ươm mầm nu&ocirc;i dưỡng c&aacute;c nh&agrave; địa chất t&iacute; hon.</p>\r\n', 1, 47),
(126, 'Ta Mù Tịt', '149', '129253', 12, '1715110934_60.jpg.webp', '', '<p>Ng&agrave;y 10 th&aacute;ng Tư năm 2019, Cơ quan H&agrave;ng kh&ocirc;ng Vũ trụ Hoa Kỳ (NASA) c&ocirc;ng bố bức ảnh về lỗ đen nằm ở trung t&acirc;m thi&ecirc;n h&agrave; Messier 87 (M87) c&aacute;ch Tr&aacute;i đất khoảng 55 triệu năm &aacute;nh s&aacute;ng, đ&aacute;nh dấu một bước tiến trong lịch sử kh&aacute;m ph&aacute; vũ trụ của con người.<br />\r\n<br />\r\nNhưng bạn kh&ocirc;ng cần phải đi xa như thế mới c&oacute; thể t&igrave;m hiểu về vũ trụ. Ch&uacute;ng ta c&ograve;n v&ocirc; v&agrave;n những điều kh&ocirc;ng biết về khoảng kh&ocirc;ng bao la ngo&agrave;i kia, những c&acirc;u hỏi lớn m&agrave; bạn c&oacute; thể nghĩ l&agrave; ta đ&atilde; c&oacute; đ&aacute;p &aacute;n nhưng thực ra lại chưa: Vũ trụ được tạo th&agrave;nh từ c&aacute;i g&igrave;? Th&agrave;nh phần cơ bản nhất của vật chất l&agrave; g&igrave;? Ch&uacute;ng tương t&aacute;c với nhau như thế n&agrave;o? Hay đơn giản hơn, bạn c&oacute; chắc m&igrave;nh biết r&otilde; c&aacute;c kh&aacute;i niệm về khối lượng, chiều kh&ocirc;ng gian, thời kh&ocirc;ng?<br />\r\n<br />\r\nBạn sẵn s&agrave;ng chưa? Bởi v&igrave; như Henry Reich, người s&aacute;ng lập ra MinutePhysics, đ&atilde; nhận x&eacute;t về cuốn s&aacute;ch n&agrave;y, &ldquo;bạn kh&ocirc;ng thường xuy&ecirc;n bắt gặp một cuộc kh&aacute;m ph&aacute; đầy lạc quan v&agrave; vui nhộn như vậy về sự ngu ngốc của con người đ&acirc;u.&rdquo;<br />\r\n<br />\r\n&ldquo;Một cuốn s&aacute;ch sống động, bất khả tri về vật l&yacute; v&agrave; những thứ nằm ngo&agrave;i vật l&yacute; Một tổng hợp gi&agrave;u t&iacute;nh giải tr&iacute; v&agrave; gi&aacute;o dục d&agrave;nh cho bất cứ ai đang t&igrave;m c&aacute;ch trau dồi hay bổ sung kiến thức của bản th&acirc;n.&rdquo;<br />\r\n- Kirkus Reviews</p>\r\n', 1, 47),
(127, 'Chiếc Thìa Biến Mất', '150', '98000', 24, '1715111006_61.jpg.webp', '', '<p><strong>Cuốn S&aacute;ch Tổng Hợp Kiến Thức Hay: Chiếc Th&igrave;a Biến Mất</strong></p>\r\n\r\n<p>&quot;Với những ai từng băn khoăn về c&acirc;u hỏi thế giới n&agrave;y bắt nguồn từ đ&acirc;u, vạn vật được tạo ra v&agrave; vận h&agrave;nh như thế n&agrave;o, cuốn s&aacute;ch n&agrave;y l&agrave; d&agrave;nh cho bạn. Trong CHIẾC TH&Igrave;A BIẾN MẤT, những kiến thức h&oacute;a học tưởng chừng kh&oacute; nhằn v&agrave; cứng nhắc đ&atilde; được diễn giải một c&aacute;ch mềm mại, gần gũi v&agrave; thực sự đời thường qua những c&acirc;u chuyện về lịch sử, c&aacute;c truyền thuyết b&iacute; ẩn v&agrave; cuộc đời của những nh&agrave; khoa học. Rồi bạn sẽ nhận ra tất cả những thứ xung quanh m&igrave;nh, kể cả những gi&aacute; trị tinh thần như cảm gi&aacute;c hạnh ph&uacute;c, bất hạnh v&agrave; đ&ocirc;i khi l&agrave; ch&iacute;nh mạng sống của bạn cũng được quyết định bởi ch&uacute;ng - những nguy&ecirc;n tố h&oacute;a học.&quot;</p>\r\n', 1, 47),
(128, 'Phiêu Lưu Kỹ Sự', '151', '191300', 31, '1715111423_62.jpg.webp', '<p>Minh họa bởi Đậu Đũa, tạo n&eacute;t đặc trưng cho truyện.</p>\r\n\r\n<p>Tạo h&igrave;nh nh&acirc;n vật mới lạ, gần gũi với độc giả.</p>\r\n\r\n<p>Thế giới sắc m&agrave;u ngọt ng&agrave;o, b&iacute; ẩn v&agrave; k&iacute;ch th&iacute;ch tr&iacute; tưởng tượng.</p>\r\n', '<p>Lần đầu ti&ecirc;n &ldquo;Dế m&egrave;n phi&ecirc;u lưu k&yacute;&rdquo; được minh họa bởi một nữ họa sĩ - họa sĩ Đậu Đũa.</p>\r\n\r\n<p>Xuất ph&aacute;t từ đồ &aacute;n tốt nghiệp, sau 7 năm theo đuổi v&agrave; ph&aacute;t triển &yacute; tưởng, họa sĩ Đậu Đũa &ldquo;tr&igrave;nh l&agrave;ng&rdquo; hơn 100 bức tranh minh họa m&agrave;u nước được vẽ tay thể hiện một c&aacute;i nh&igrave;n ho&agrave;n to&agrave;n mới về &ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo;.</p>\r\n\r\n<p>L&agrave; họa sĩ tuổi 9X - Đậu Đũa đ&atilde; vẽ &ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo; bằng con mắt của những c&ocirc; b&eacute; cậu b&eacute; thời hiện đại. C&ocirc; ho&agrave;n to&agrave;n tho&aacute;t khỏi tạo h&igrave;nh Dế m&egrave;n của c&aacute;c thế hệ họa sĩ đi trước.</p>\r\n\r\n<p>C&aacute;c nh&acirc;n vật &ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo; lần đầu được vận l&ecirc;n người bộ &acirc;u phục thời trang lịch l&atilde;m. Tạo h&igrave;nh n&agrave;y kh&ocirc;ng chỉ l&agrave;m n&ecirc;n sự mới mẻ với những ai đ&atilde; biết đến &ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo; m&agrave; c&ograve;n tạo sự gần gũi với độc giả đương đại chưa từng đọc &ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo;</p>\r\n\r\n<p>Họa sĩ Đậu Đũa đ&atilde; tạo n&ecirc;n một thế giới sắc m&agrave;u ngọt ng&agrave;o quyến rũ, vừa xa lạ b&iacute; ẩn vừa k&iacute;ch th&iacute;ch tr&iacute; tưởng tượng v&agrave; kh&aacute;t khao cất bước phi&ecirc;u lưu</p>\r\n\r\n<p>Đậu Đũa đ&atilde; bắt được c&aacute;i tinh thần &ldquo;Đi khắp thế giới kết anh em&rdquo; của &ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo;. C&oacute; thể n&oacute;i &ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo; minh họa Đậu Đũa l&agrave; c&aacute;i nh&igrave;n to&agrave;n cầu. Mọi h&igrave;nh ảnh đều vừa thực vừa tho&aacute;t khỏi khu&ocirc;n mẫu tả thực. Dế M&egrave;n, Dế Trũi, C&agrave;o C&agrave;o, Bọ Muỗm&hellip; kh&ocirc;ng c&ograve;n l&agrave; những c&ocirc;n tr&ugrave;ng m&agrave; thực sự l&agrave; những nh&acirc;n vật h&oacute;a trang kể những c&acirc;u chuyện của thế giới lo&agrave;i người. Đ&uacute;ng với t&iacute;nh chất của truyện đồng thoại.</p>\r\n\r\n<p>&ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo; l&agrave; t&aacute;c phẩm Việt Nam được dịch ra nhiều thứ tiếng nhất, NXB Kim Đồng tin rằng với minh họa Đậu Đũa, &ldquo;Dế M&egrave;n phi&ecirc;u lưu k&yacute;&rdquo; sẽ ph&ugrave; hợp xu thế hội nhập.</p>\r\n\r\n<p>Họa sĩ ĐẬU ĐŨA</p>\r\n\r\n<p>Bén duy&ecirc;n với ch&uacute; Dế M&egrave;n của b&aacute;c Tô Ho&agrave;i năm 2014 trong đồ &aacute;n tốt nghiệp (Khoa Mỹ thuật Công nghiệp, Đại học Kiến tr&uacute;c Tp. Hồ Ch&iacute; Minh). Đến năm 2020 thì việc minh họa cho cuốn s&aacute;ch n&agrave;y được ho&agrave;n th&agrave;nh. Một cuộc phi&ecirc;u lưu kh&aacute; d&agrave;i v&agrave; vô cùng th&uacute; vị, không kém gì ch&agrave;ng Dế trong truyện.</p>\r\n\r\n<p>* Giải Nhất cuộc thi Samsung KidsTime Authors&rsquo; Award 2015 cho t&aacute;c phẩm: Tr&aacute;i tim của Mẹ.</p>\r\n\r\n<p>* Giải Bạc - S&aacute;ch hay của Giải thưởng s&aacute;ch Việt Nam 2016 cho t&aacute;c phẩm: Tr&aacute;i tim của Mẹ.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 38),
(129, 'Nhắm Mắt Mở Cửa', '152', '75000', 50, '1715111608_63.jpg.webp', '', '<p>Tập s&aacute;ch hay, dễ thương, v&agrave; c&ograve;n nhiều mỹ từ kh&aacute;c nữa xứng đ&aacute;ng được d&agrave;nh cho n&oacute;. H&atilde;y t&igrave;m đọc nội dung thay v&igrave; đọc trước phần giới thiệu s&aacute;ch n&agrave;y viết g&igrave;&hellip;. Như thế, bạn sẽ c&agrave;ng th&iacute;ch th&uacute; hơn với &ldquo;Vừa nhắm mắt, vừa mở cửa sổ&rdquo;.</p>\r\n', 1, 38),
(130, 'Wombi Thăm Xứ Tuyết', '153', '76300', 27, '1715111721_64.jpg.webp', '', '<p>Trời n&oacute;ng nực qu&aacute;, gấu t&uacute;i mũi trần Wombi tu kiệt cả b&igrave;nh nước m&agrave; mồ h&ocirc;i vẫn cứ t&uacute;a ra đầm đ&igrave;a. Cậu ao ước được đến Phần Lan để gặp tuần lộc, &ocirc;ng gi&agrave; Noel, v&agrave; lăn lộn trong tuyết m&aacute;t lạnh đ&atilde; đời. Qu&atilde;ng đường từ &Uacute;c đến xứ tuyết mu&ocirc;n tr&ugrave;ng xa x&ocirc;i. Nhưng với sự gi&uacute;p đỡ của những người bạn kỳ l&agrave; tốt bụng như th&uacute; mỏ vịt, nai sừng tấm, gia đ&igrave;nh s&oacute;i, giấc mơ của Wombi đ&atilde; trở th&agrave;nh hiện thực. Cậu đến xứ tuyết vừa kịp Gi&aacute;ng Sinh!</p>\r\n\r\n<p>Cuốn s&aacute;ch chứa đựng đầy những kiến thức th&uacute; vị về &Uacute;c v&agrave; Phần Lan sẽ l&agrave; m&oacute;n qu&agrave; Gi&aacute;ng Sinh tuyệt vời cho c&aacute;c bạn nhỏ mầm non v&agrave; tiểu học!</p>\r\n', 1, 38),
(131, 'Phù Thuỷ Xứ Oz', '154', '48500', 23, '1715111853_65.jpg.webp', '', '<p>&ldquo;Dorothy sống giữa v&ugrave;ng đồng cỏ Kansas bao la&hellip;&rdquo; Bắt đầu như thế&nbsp; l&agrave; cuốn truyện thiếu nhi kinh điển của nước Mỹ. Cơn lốc xo&aacute;y đ&atilde; v&ocirc; t&igrave;nh cuốn Dorothy c&ugrave;ng ch&uacute; ch&oacute; nhỏ Toto đến một nơi lạ l&ugrave;ng, t&ecirc;n gọi xứ Oz; v&agrave; c&ocirc; b&eacute; sợ rằng m&igrave;nh chẳng bao giờ được về với Ch&uacute; Henry, D&igrave; Em trong căn nh&agrave; x&aacute;m giữa đồng cỏ bao la.</p>\r\n\r\n<p>Nhưng c&ocirc; đ&atilde; gặp những người Munchkin, họ chỉ cho c&ocirc; Con đường Gạch v&agrave;ng dẫn tới Th&agrave;nh phố Ngọc Lục bảo, nơi c&oacute; Oz, vị Ph&ugrave; thủy c&oacute; khả năng thỏa m&atilde;n mọi điều ước. Tr&ecirc;n đường đi, c&ocirc; gặp B&ugrave; nh&igrave;n rơm, Thợ rừng Thiếc, Sư tử Nh&aacute;t, tất cả đồng l&ograve;ng c&ugrave;ng c&ocirc; dấn bước v&agrave;o phi&ecirc;u lưu, xuy&ecirc;n qua đồng anh t&uacute;c chết ch&oacute;c, đối mặt c&aacute;c qu&aacute;i th&uacute;, đối mặt ph&ugrave; thủy &Aacute;c&hellip; v&agrave; cả&nbsp;<strong>Ph&ugrave; thủy xứ Oz&nbsp;</strong>h&ugrave;ng mạnh, để t&igrave;m ra ch&igrave;a kh&oacute;a cho điều họ mong mỏi nhất&hellip;</p>\r\n', 1, 38);

